import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Menu, X } from 'lucide-react'; // Icons for mobile menu

const LandingPage = () => {
  const navigate = useNavigate();

  // 1. RESPONSIVE STATE
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const styles = {
    container: {
      fontFamily: "'Inter', sans-serif",
      color: '#333',
      backgroundColor: '#fff',
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      overflowX: 'hidden'
    },

    // --- NAVBAR ---
    navbar: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: isMobile ? '2vh 5vw' : '2vh 4vw',
      backgroundColor: '#fff',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      boxShadow: '0 0.5vh 2vh rgba(0,0,0,0.05)',
      height: '10vh'
    },
    logoContainer: {
      display: 'flex',
      alignItems: 'center',
      gap: '1vw',
      cursor: 'pointer'
    },
    logoIcon: {
      width: isMobile ? '8vw' : '2.5vw',
      height: isMobile ? '8vw' : '2.5vw',
      borderRadius: '0.5vw',
      objectFit: 'cover'
    },
    logoText: {
      fontSize: isMobile ? '5vw' : '1.5vw',
      fontWeight: '800',
      color: '#1f2937'
    },

    // Desktop Nav Links
    navLinks: {
      display: isMobile ? 'none' : 'flex',
      gap: '2vw'
    },
    link: {
      textDecoration: 'none',
      color: '#4b5563',
      fontSize: '1vw',
      fontWeight: '500',
      cursor: 'pointer',
      transition: 'color 0.2s'
    },

    // Auth Buttons (Desktop)
    authButtons: {
      display: isMobile ? 'none' : 'flex',
      gap: '1vw',
      alignItems: 'center'
    },
    loginBtn: {
      background: 'none',
      border: 'none',
      fontSize: '1vw',
      fontWeight: '600',
      cursor: 'pointer',
      color: '#1f2937'
    },
    signupBtn: {
      padding: '1vh 1.5vw',
      backgroundColor: '#2563eb',
      color: '#fff',
      border: 'none',
      borderRadius: '0.5vw',
      fontSize: '1vw',
      fontWeight: '600',
      cursor: 'pointer'
    },

    // Mobile Menu Button
    menuBtn: {
      display: isMobile ? 'block' : 'none',
      background: 'none',
      border: 'none',
      cursor: 'pointer'
    },

    // --- HERO SECTION ---
    hero: {
      flex: 1,
      background: 'linear-gradient(135deg, #378b5a 0%, #1e5a38 100%)',
      display: 'flex',
      flexDirection: isMobile ? 'column' : 'row', // Stack on mobile
      alignItems: 'center',
      justifyContent: 'center',
      padding: isMobile ? '5vh 5vw' : '0 8vw',
      color: '#fff',
      gap: isMobile ? '5vh' : '4vw',
      minHeight: '90vh',
      textAlign: isMobile ? 'center' : 'left'
    },
    heroContent: {
      flex: 1,
      maxWidth: isMobile ? '100%' : '40vw',
      display: 'flex',
      flexDirection: 'column',
      alignItems: isMobile ? 'center' : 'flex-start'
    },
    title: {
      fontSize: isMobile ? '8vw' : '3.5vw',
      fontWeight: '800',
      marginBottom: '2vh',
      lineHeight: '1.2'
    },
    subtitle: {
      fontSize: isMobile ? '4vw' : '1.2vw',
      opacity: 0.9,
      marginBottom: '4vh',
      lineHeight: '1.6',
      maxWidth: isMobile ? '90%' : '100%'
    },
    heroBtn: {
      padding: isMobile ? '2vh 8vw' : '1.5vh 2.5vw',
      backgroundColor: '#2563eb',
      color: '#fff',
      border: 'none',
      borderRadius: isMobile ? '2vw' : '0.6vw',
      fontSize: isMobile ? '4vw' : '1.2vw',
      fontWeight: 'bold',
      cursor: 'pointer',
      boxShadow: '0 0.5vh 1vh rgba(0,0,0,0.2)'
    },

    // Hero Image
    heroImageContainer: {
      flex: 1,
      maxWidth: isMobile ? '90vw' : '40vw',
      height: isMobile ? '30vh' : 'auto',
      borderRadius: isMobile ? '4vw' : '1.5vw',
      overflow: 'hidden',
      boxShadow: '0 2vh 4vh rgba(0,0,0,0.3)',
      marginTop: isMobile ? '2vh' : '0'
    },
    heroImage: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
      display: 'block'
    },

    // Mobile Menu Overlay
    mobileMenu: {
      display: menuOpen ? 'flex' : 'none',
      position: 'fixed',
      top: '10vh',
      left: 0,
      width: '100%',
      height: '90vh',
      backgroundColor: '#fff',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '4vh',
      zIndex: 999
    },
    mobileLink: {
      fontSize: '5vw',
      color: '#333',
      textDecoration: 'none',
      fontWeight: '600'
    },
    mobileAuthBtn: {
      padding: '2vh 10vw',
      backgroundColor: '#2563eb',
      color: '#fff',
      border: 'none',
      borderRadius: '2vw',
      fontSize: '5vw',
      fontWeight: 'bold'
    }
  };

  return (
    <div style={styles.container}>

      {/* NAVBAR */}
      <nav style={styles.navbar}>
        <div style={styles.logoContainer} onClick={() => navigate('/')}>
          <img 
            src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" // Placeholder logo
            alt="Logo" 
            style={styles.logoIcon} 
          />
          <span style={styles.logoText}>EventSphere</span>
        </div>

        {/* Desktop Links */}
        <div style={styles.navLinks}>
          <span style={styles.link}>Home</span>
          <span style={styles.link}>Events</span>
          <span style={styles.link}>Workshops</span>
          <span style={styles.link}>About</span>
        </div>

        {/* Desktop Auth */}
        <div style={styles.authButtons}>
          <button style={styles.loginBtn} onClick={() => navigate('/signin')}>Log in</button>
          <button style={styles.signupBtn} onClick={() => navigate('/signup')}>Sign up free</button>
        </div>

        {/* Mobile Menu Toggle */}
        <button style={styles.menuBtn} onClick={() => setMenuOpen(!menuOpen)}>
          {menuOpen ? <X size={28} /> : <Menu size={28} />}
        </button>
      </nav>

      {/* Mobile Menu Overlay */}
      {isMobile && (
        <div style={styles.mobileMenu}>
          <span style={styles.mobileLink} onClick={() => setMenuOpen(false)}>Home</span>
          <span style={styles.mobileLink} onClick={() => setMenuOpen(false)}>Events</span>
          <span style={styles.mobileLink} onClick={() => setMenuOpen(false)}>Workshops</span>
          <span style={styles.mobileLink} onClick={() => setMenuOpen(false)}>About</span>
          <button style={styles.mobileAuthBtn} onClick={() => navigate('/signin')}>Log In</button>
          <button style={styles.mobileAuthBtn} onClick={() => navigate('/signup')}>Sign Up</button>
        </div>
      )}

      {/* HERO SECTION */}
      <header style={styles.hero}>
        <div style={styles.heroContent}>
          <h1 style={styles.title}>
            <span style={{color: '#4ade80'}}>EVENTSPHERE</span> <br />
            The Ultimate Campus Event Platform!
          </h1>
          <p style={styles.subtitle}>
            Streamline event planning, boost student engagement, and simplify 
            venue booking. The all-in-one platform built for modern universities.
          </p>
          <button style={styles.heroBtn} onClick={() => navigate('/signup')}>Get Started</button>
        </div>

        <div style={styles.heroImageContainer}>
          <img 
            src="https://images.unsplash.com/photo-1492684223066-81342ee5ff30?w=800&q=80" 
            alt="Event Crowd" 
            style={styles.heroImage} 
          />
        </div>
      </header>

    </div>
  );
};

export default LandingPage;
