import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Search, Clock, MapPin, ArrowLeft } from 'lucide-react'; 
import EventDetails from './EventDetails'; 

const Events = ({ theme, allEvents = [], searchTerm, setSearchTerm, onRegister }) => {
  const navigate = useNavigate();
  const [activeCategory, setActiveCategory] = useState("All");
  const [selectedEvent, setSelectedEvent] = useState(null); // Tracks the event for the Detail Slide
  
  const isDark = theme === 'dark';
  const categories = ["All", "Tech", "Cultural", "Sports", "Workshops"];

  const displayedEvents = allEvents.filter(event => {
    const matchesCategory = activeCategory === "All" || event.category === activeCategory;
    const matchesSearch = event.title.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const styles = {
    container: { backgroundColor: isDark ? '#0f172a' : '#f8fafc', height: '100vh', display: 'flex', flexDirection: 'column' },
    header: { padding: '16px 16px 10px 16px', backgroundColor: isDark ? '#0f172a' : '#fff' },
    searchBar: { display: 'flex', alignItems: 'center', backgroundColor: isDark ? '#1e293b' : '#f1f5f9', padding: '10px 16px', borderRadius: '12px', marginBottom: '16px' },
    filterScroll: { display: 'flex', gap: '10px', overflowX: 'auto', paddingBottom: '5px', msOverflowStyle: 'none', scrollbarWidth: 'none' },
    scrollArea: { flex: 1, overflowY: 'auto', padding: '0 16px 100px 16px' }
  };

  return (
    <div style={styles.container}>
      <style>{`.hide-scrollbar::-webkit-scrollbar { display: none; }`}</style>

      <div style={styles.header}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '16px' }}>
          <ArrowLeft 
            size={24} 
            onClick={() => navigate('/')} 
            style={{ cursor: 'pointer', color: isDark ? '#fff' : '#1e293b' }} 
          />
          <h2 style={{ margin: 0, color: isDark ? '#fff' : '#1e293b', fontSize: '24px' }}>Events</h2>
        </div>
        
        <div style={styles.searchBar}>
          <Search size={18} color="#94a3b8" />
          <input 
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search events..." 
            style={{ border: 'none', background: 'transparent', marginLeft: '10px', flex: 1, color: isDark ? '#fff' : '#000', outline: 'none' }} 
          />
        </div>

        <div style={styles.filterScroll} className="hide-scrollbar">
          {categories.map(cat => (
            <button 
              key={cat}
              onClick={() => setActiveCategory(cat)}
              style={{
                padding: '8px 20px', borderRadius: '10px', border: 'none',
                backgroundColor: activeCategory === cat ? '#5c5cfc' : (isDark ? '#1e293b' : '#f1f5f9'),
                color: activeCategory === cat ? '#fff' : '#94a3b8',
                whiteSpace: 'nowrap', cursor: 'pointer', fontWeight: 'bold', fontSize: '13px'
              }}
            >
              {cat}
            </button>
          ))}
        </div>
      </div>

      <div style={styles.scrollArea}>
        {displayedEvents.length > 0 ? (
          displayedEvents.map(event => (
            <div key={event.id} style={{ backgroundColor: isDark ? '#1e293b' : '#fff', borderRadius: '20px', marginBottom: '20px', overflow: 'hidden', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
              <img 
                src={event.image} 
                alt={event.title} 
                style={{ width: '100%', height: '180px', objectFit: 'cover' }} 
              />
              <div style={{ padding: '16px' }}>
                <h3 style={{ margin: '0 0 8px 0', color: isDark ? '#fff' : '#1e293b' }}>{event.title}</h3>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div style={{ fontSize: '12px', color: '#94a3b8' }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '4px' }}><Clock size={14}/> {event.date}</div>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '4px', marginTop: '4px' }}><MapPin size={14}/> {event.location}</div>
                  </div>
                  {/* Register Button triggers the Detail Slide */}
                  <button 
                    onClick={() => setSelectedEvent(event)}
                    style={{ 
                      backgroundColor: event.isRegistered ? '#16a34a' : '#5c5cfc', 
                      color: '#fff', border: 'none', padding: '8px 20px', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer' 
                    }}
                  >
                    {event.isRegistered ? "✓ Registered" : "Register"}
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p style={{ textAlign: 'center', color: '#94a3b8', marginTop: '40px' }}>No events found.</p>
        )}
      </div>

      {/* RENDER THE SLIDE-UP PANEL */}
      {selectedEvent && (
        <EventDetails 
          event={selectedEvent} 
          theme={theme}
          onClose={() => setSelectedEvent(null)} 
          onRegister={onRegister}
        />
      )}
    </div>
  );
};

export default Events;
