import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
// ... other imports

function App() {
  const [theme, setTheme] = useState("light"); // Global theme state
  const [searchTerm, setSearchTerm] = useState("");
  const [user, setUser] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2023-4589"
  });

  // Theme-based dynamic styles
  const appStyles = {
    maxWidth: '375px',
    margin: '0 auto',
    backgroundColor: theme === 'light' ? '#fff' : '#0f172a',
    color: theme === 'light' ? '#1e293b' : '#f8fafc',
    minHeight: '100vh',
    position: 'relative',
    transition: 'all 0.3s ease'
  };

  return (
    <Router>
      <div style={appStyles}>
        <Routes>
          <Route path="/" element={<Dashboard theme={theme} user={user} events={filteredEvents} searchTerm={searchTerm} setSearchTerm={setSearchTerm} regCount={registeredList.length} />} />
          {/* Pass theme and setTheme to the selection page */}
          <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
          <Route path="/profile" element={<Profile theme={theme} user={user} />} />
          {/* ... other routes */}
        </Routes>
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
