import React, { useState } from 'react';
import { Ticket, MapPin, Calendar, Trash2, QrCode, AlertTriangle, Clock } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const MyEvents = ({ theme, events, onCancel }) => { 
  const navigate = useNavigate();
  const isDark = theme === 'dark';
  const [showModal, setShowModal] = useState(false);
  const [selectedEventId, setSelectedEventId] = useState(null);

  const selectedEvent = events.find(e => e.id === selectedEventId);

  const handleInitiateCancel = (eventId) => {
    setSelectedEventId(eventId);
    setShowModal(true);
  };

  const handleConfirmCancel = () => {
    if (selectedEventId) {
      onCancel(selectedEventId);
      setShowModal(false);
      setSelectedEventId(null);
    }
  };

  const styles = {
    container: { padding: '20px', backgroundColor: isDark ? '#0f172a' : '#fff', minHeight: '100vh', paddingBottom: '120px', boxSizing: 'border-box', position: 'relative' },
    title: { color: isDark ? '#fff' : '#1e293b', marginBottom: '25px', fontSize: '24px', fontWeight: 'bold' },
    card: { backgroundColor: isDark ? '#1e293b' : '#fff', borderRadius: '20px', padding: '20px', marginBottom: '20px', border: '1px solid #e2e8f0', boxShadow: '0 4px 6px rgba(0,0,0,0.05)', position: 'relative', display: 'flex', flexDirection: 'column', gap: '15px' },
    cardHeader: { display: 'flex', gap: '15px', alignItems: 'flex-start' },
    iconBox: (status) => ({
      backgroundColor: status === 'approved' ? '#5c5cfc' : '#f59e0b', // Blue for approved, Orange for pending
      color: '#fff', padding: '12px', borderRadius: '12px', display: 'flex', alignItems: 'center', justifyContent: 'center', height: 'fit-content'
    }),
    eventTitle: { margin: '0 0 5px 0', color: isDark ? '#fff' : '#1e293b', fontSize: '18px', fontWeight: 'bold' },
    eventDate: { margin: 0, fontSize: '13px', color: '#94a3b8', display: 'flex', alignItems: 'center', gap: '5px', marginBottom: '4px' },
    
    // Status Badge
    statusBadge: (status) => ({
      display: 'inline-block',
      padding: '4px 10px',
      borderRadius: '12px',
      fontSize: '11px',
      fontWeight: 'bold',
      marginTop: '5px',
      backgroundColor: status === 'approved' ? '#dcfce7' : '#fef3c7',
      color: status === 'approved' ? '#16a34a' : '#d97706'
    }),

    actionButtons: { display: 'flex', gap: '10px', marginTop: '5px' },
    
    // View Ticket Button (Only for approved)
    viewBtn: { flex: 1, backgroundColor: '#5c5cfc', color: '#fff', border: 'none', padding: '12px', borderRadius: '12px', fontWeight: 'bold', fontSize: '14px', cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' },
    
    // Pending Button (Disabled)
    pendingBtn: { flex: 1, backgroundColor: '#f1f5f9', color: '#94a3b8', border: 'none', padding: '12px', borderRadius: '12px', fontWeight: 'bold', fontSize: '14px', cursor: 'not-allowed', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' },
    
    cancelBtn: { flex: 1, backgroundColor: '#fee2e2', color: '#ef4444', border: 'none', padding: '12px', borderRadius: '12px', fontWeight: 'bold', fontSize: '14px', cursor: 'pointer', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' },
    
    // Modal Styles... (Same as before)
    overlay: { position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', backgroundColor: 'rgba(0,0,0,0.6)', zIndex: 2000, display: 'flex', alignItems: 'flex-end', justifyContent: 'center' },
    modalContent: { backgroundColor: isDark ? '#1e293b' : '#fff', width: '100%', maxWidth: '430px', borderTopLeftRadius: '24px', borderTopRightRadius: '24px', padding: '30px 24px', textAlign: 'center', animation: 'slideUp 0.3s ease-out', boxShadow: '0 -10px 40px rgba(0,0,0,0.2)' },
    modalTitle: { fontSize: '20px', fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b', marginBottom: '10px' },
    modalText: { fontSize: '14px', color: '#64748b', marginBottom: '24px', lineHeight: '1.5' },
    modalBtnDanger: { width: '100%', backgroundColor: '#fee2e2', color: '#ef4444', padding: '16px', borderRadius: '16px', border: 'none', fontSize: '16px', fontWeight: 'bold', cursor: 'pointer', marginBottom: '12px' },
    modalBtnCancel: { width: '100%', backgroundColor: isDark ? '#334155' : '#f1f5f9', color: isDark ? '#fff' : '#64748b', padding: '16px', borderRadius: '16px', border: 'none', fontSize: '16px', fontWeight: 'bold', cursor: 'pointer' }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>My Status</h2>

      {events.length > 0 ? (
        events.map(event => (
          <div key={event.id} style={styles.card}>
            <div style={styles.cardHeader}>
              <div style={styles.iconBox(event.status)}>
                {event.status === 'approved' ? <Ticket size={24} /> : <Clock size={24} />}
              </div>
              <div style={{ flex: 1 }}>
                <h3 style={styles.eventTitle}>{event.title}</h3>
                <p style={styles.eventDate}><Calendar size={12} /> {event.date}</p>
                <span style={styles.statusBadge(event.status)}>
                  {event.status === 'approved' ? 'REGISTRATION APPROVED' : 'WAITING FOR APPROVAL'}
                </span>
              </div>
            </div>

            <div style={styles.actionButtons}>
              {event.status === 'approved' ? (
                <button style={styles.viewBtn} onClick={() => navigate(`/ticket-confirmation/${event.id}`)}>
                  <QrCode size={16} /> View Ticket
                </button>
              ) : (
                <button style={styles.pendingBtn}>
                  <Clock size={16} /> Processing...
                </button>
              )}

              <button style={styles.cancelBtn} onClick={() => handleInitiateCancel(event.id)}>
                <Trash2 size={16} /> Cancel
              </button>
            </div>
          </div>
        ))
      ) : (
        <div style={{ textAlign: 'center', marginTop: '100px', color: '#94a3b8' }}>
          <Ticket size={64} style={{ marginBottom: '15px', opacity: 0.3 }} />
          <p style={{ fontSize: '18px' }}>No registrations yet</p>
        </div>
      )}

      {/* MODAL */}
      {showModal && (
        <div style={styles.overlay}>
          <div style={styles.modalContent}>
             <div style={{ width: '50px', height: '50px', backgroundColor: '#fee2e2', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto 15px auto' }}>
              <AlertTriangle size={24} color="#ef4444" />
            </div>
            <h3 style={styles.modalTitle}>Cancel Registration?</h3>
            <p style={styles.modalText}>
              Are you sure you want to cancel for <br/>
              <strong style={{ color: isDark ? '#fff' : '#000' }}>{selectedEvent?.title}</strong>?
            </p>
            <button style={styles.modalBtnDanger} onClick={handleConfirmCancel}>Yes, Cancel It</button>
            <button style={styles.modalBtnCancel} onClick={() => setShowModal(false)}>No, Keep It</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyEvents;
