import React, { useState } from 'react';
import { 
  Home, Calendar, Ticket, User, Settings, LogOut, Menu 
} from 'lucide-react';
import { useNavigate, useLocation } from 'react-router-dom';

const Sidebar = ({ user, theme, onLogout }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isDark = theme === 'dark';
  
  // State: Default to collapsed (false) or expanded (true) as you prefer
  const [isExpanded, setIsExpanded] = useState(false);

  const menuItems = [
    { label: 'Dashboard', icon: <Home size={22} />, path: '/' },
    { label: 'Events', icon: <Calendar size={22} />, path: '/events' },
    { label: 'My Tickets', icon: <Ticket size={22} />, path: '/my-events' },
    { label: 'Profile', icon: <User size={22} />, path: '/profile' },
    { label: 'Settings', icon: <Settings size={22} />, path: '/settings' },
  ];

  const styles = {
    // 1. SIDEBAR CONTAINER
    sidebar: {
      height: '100vh',
      width: isExpanded ? '260px' : '70px', // Width changes here
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRight: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      display: 'flex',
      flexDirection: 'column',
      padding: '20px 12px', // Less horizontal padding when collapsed
      position: 'fixed',
      left: 0,
      top: 0,
      zIndex: 1000,
      transition: 'width 0.3s ease', // Smooth animation
      overflowX: 'hidden', // Hide content that overflows during animation
    },

    // 2. TOGGLE BUTTON & LOGO AREA
    topSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      marginBottom: '40px',
      paddingLeft: '5px',
      height: '40px' // Fixed height to prevent jumping
    },
    menuBtn: {
      cursor: 'pointer',
      color: isDark ? '#fff' : '#1e293b',
      minWidth: '24px', // Ensure icon doesn't shrink
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    logoText: {
      fontSize: '20px',
      fontWeight: '800',
      color: '#6366f1',
      whiteSpace: 'nowrap', // Keeps text on one line
      opacity: isExpanded ? 1 : 0, // Fade out
      transform: isExpanded ? 'translateX(0)' : 'translateX(-20px)', // Slide effect
      transition: 'all 0.3s ease',
      // If collapsed, completely hide from layout flow after transition
      visibility: isExpanded ? 'visible' : 'hidden' 
    },

    // 3. NAVIGATION ITEMS
    nav: {
      flex: 1,
      display: 'flex',
      flexDirection: 'column',
      gap: '10px'
    },
    navItem: (isActive) => ({
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      padding: '12px',
      borderRadius: '12px',
      cursor: 'pointer',
      backgroundColor: isActive ? (isDark ? '#334155' : '#eff6ff') : 'transparent',
      color: isActive ? '#6366f1' : (isDark ? '#94a3b8' : '#64748b'),
      transition: 'all 0.2s',
      overflow: 'hidden',
      whiteSpace: 'nowrap'
    }),
    navLabel: {
      fontWeight: '600',
      fontSize: '15px',
      opacity: isExpanded ? 1 : 0,
      transition: 'opacity 0.2s ease',
      display: isExpanded ? 'block' : 'none'
    },

    // 4. BOTTOM USER SECTION
    userSection: {
      marginTop: 'auto',
      borderTop: isDark ? '1px solid #334155' : '1px solid #f1f5f9',
      paddingTop: '20px',
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      overflow: 'hidden'
    },
    avatar: {
      width: '36px',
      height: '36px',
      borderRadius: '50%',
      objectFit: 'cover',
      minWidth: '36px' // Prevent shrinking
    },
    userDetails: {
      opacity: isExpanded ? 1 : 0,
      transition: 'opacity 0.2s',
      whiteSpace: 'nowrap',
      display: isExpanded ? 'block' : 'none'
    }
  };

  return (
    <>
      {/* Spacer to push main content to the right */}
      <div style={{ width: isExpanded ? '260px' : '70px', transition: 'width 0.3s ease' }}></div>

      <div style={styles.sidebar}>
        
        {/* TOP: Menu Icon + Logo Text */}
        <div style={styles.topSection}>
          <div style={styles.menuBtn} onClick={() => setIsExpanded(!isExpanded)}>
            <Menu size={26} />
          </div>
          <div style={styles.logoText}>EventSphere</div>
        </div>

        {/* MIDDLE: Navigation Menu */}
        <div style={styles.nav}>
          {menuItems.map((item) => {
            const isActive = location.pathname === item.path;
            return (
              <div 
                key={item.label} 
                style={styles.navItem(isActive)} 
                onClick={() => navigate(item.path)}
                title={!isExpanded ? item.label : ''} // Tooltip on hover when collapsed
              >
                {/* Icon is always visible */}
                <div>{item.icon}</div>
                {/* Label hides when collapsed */}
                <span style={styles.navLabel}>{item.label}</span>
              </div>
            );
          })}
        </div>

        {/* BOTTOM: User & Logout */}
        <div style={styles.userSection}>
          <img src={user.profileImage} alt="User" style={styles.avatar} />
          <div style={styles.userDetails}>
            <div style={{fontWeight: 'bold', fontSize: '14px', color: isDark?'#fff':'#333'}}>
              {user.name.split(' ')[0]}
            </div>
            <div style={{fontSize: '12px', color: '#64748b'}}>Student</div>
          </div>
        </div>

        {/* Logout Button */}
        <div 
          style={{ ...styles.navItem(false), marginTop: '10px', color: '#ef4444' }} 
          onClick={onLogout}
        >
          <div><LogOut size={22} /></div>
          <span style={styles.navLabel}>Log Out</span>
        </div>

      </div>
    </>
  );
};

export default Sidebar;
