import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Home, Calendar, Ticket, User } from 'lucide-react';

const BottomNav = ({ theme }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isActive = (path) => location.pathname === path;

  // Colors adapt based on the global theme
  const styles = {
    nav: {
      position: 'fixed',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '100%',
      maxWidth: '375px',
      backgroundColor: theme === 'dark' ? '#1e293b' : '#ffffff',
      display: 'flex',
      justifyContent: 'space-around',
      padding: '12px 0',
      borderTop: theme === 'dark' ? '1px solid #334155' : '1px solid #f1f5f9',
      zIndex: 1000
    },
    item: (path) => ({
      color: isActive(path) ? '#2563eb' : (theme === 'dark' ? '#94a3b8' : '#64748b'),
      textAlign: 'center',
      cursor: 'pointer'
    })
  };

  const navItems = [
    { icon: <Home size={22} />, label: 'Home', path: '/' },
    { icon: <Calendar size={22} />, label: 'Events', path: '/events' },
    { icon: <Ticket size={22} />, label: 'My Events', path: '/my-events' },
    { icon: <User size={22} />, label: 'Profile', path: '/profile' }
  ];

  return (
    <nav style={styles.nav}>
      {navItems.map((item) => (
        <div key={item.path} onClick={() => navigate(item.path)} style={styles.item(item.path)}>
          {item.icon}
          <div style={{ fontSize: '10px', marginTop: '4px' }}>{item.label}</div>
        </div>
      ))}
    </nav>
  );
};

export default BottomNav;
