import React from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, CheckCircle, Calendar, AlertCircle, Bell } from 'lucide-react';

const Notifications = ({ theme, notificationsList }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  // UPDATED DATA: Separated 'eventName' and 'status'
  const defaultNotifications = [
    { 
      id: 1, 
      type: 'success', 
      eventName: 'AI & Future Tech Workshop', // <--- MAIN BOLD TITLE
      status: 'Registration Approved',        // <--- COLORED TEXT
      desc: "Your seat has been confirmed. Tap to view your ticket.", 
      time: "2h ago", 
      eventId: 1, 
      icon: <CheckCircle size={24} color="#5c5cfc" />, 
      bgColor: '#eef2ff' 
    },
    { 
      id: 2, 
      type: 'new',
      eventName: 'Inter-College Music Battle',
      status: 'New Event Added', 
      desc: "Registration is now open! Click to see full details.", 
      time: "5h ago", 
      eventId: 2,
      icon: <Calendar size={24} color="#22c55e" />, 
      bgColor: '#dcfce7' 
    },
    { 
      id: 3, 
      type: 'update',
      eventName: 'University Basketball Finals',
      status: 'Location Updated', 
      desc: "The venue has been changed to the Indoor Sports Complex.", 
      time: "Yesterday", 
      eventId: 3,
      icon: <AlertCircle size={24} color="#eab308" />, 
      bgColor: '#fef9c3' 
    },
    { 
      id: 4, 
      type: 'reminder',
      eventName: 'AI & Future Tech Workshop',
      status: 'Event Reminder', 
      desc: "Don't forget, the event starts tomorrow at 09:00 AM.", 
      time: "2d ago", 
      eventId: 1,
      icon: <Bell size={24} color="#ef4444" />, 
      bgColor: '#fee2e2' 
    }
  ];

  const list = notificationsList && notificationsList.length > 0 ? notificationsList : defaultNotifications;

  const handleNotificationClick = (item) => {
    if (!item.eventId) return;
    if (item.type === 'success' || item.status.includes('Approved')) {
      navigate(`/ticket-confirmation/${item.eventId}`);
    } else {
      navigate(`/event-details/${item.eventId}`);
    }
  };

  const styles = {
    container: {
      padding: '40px',
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      minHeight: '100%',
      fontFamily: "'Inter', sans-serif",
      maxWidth: '800px',
      margin: '0 auto'
    },
    header: {
      display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '30px'
    },
    backBtn: {
      cursor: 'pointer', padding: '8px', borderRadius: '50%',
      backgroundColor: 'transparent', border: 'none', color: isDark ? '#fff' : '#1e293b'
    },
    title: {
      fontSize: '24px', fontWeight: '800', color: isDark ? '#fff' : '#1e293b'
    },
    listContainer: {
      display: 'flex', flexDirection: 'column', gap: '16px'
    },
    card: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '20px',
      borderRadius: '16px',
      display: 'flex', gap: '20px', alignItems: 'flex-start',
      boxShadow: '0 4px 6px rgba(0,0,0,0.02)',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      cursor: 'pointer', transition: 'transform 0.2s'
    },
    iconBox: (bg) => ({
      width: '50px', height: '50px', borderRadius: '12px',
      backgroundColor: isDark ? '#334155' : bg,
      display: 'flex', alignItems: 'center', justifyContent: 'center', flexShrink: 0
    }),
    content: { flex: 1 },
    rowTop: {
      display: 'flex', justifyContent: 'space-between', marginBottom: '4px'
    },
    // EVENT NAME (Main Title)
    eventName: {
      fontSize: '16px', fontWeight: '700', color: isDark ? '#fff' : '#1e293b'
    },
    time: {
      fontSize: '12px', color: '#94a3b8', fontWeight: '500', whiteSpace: 'nowrap', marginLeft: '10px'
    },
    // STATUS (Colored Subtitle)
    statusText: {
      fontSize: '14px', fontWeight: '600', color: '#6366f1', marginBottom: '6px'
    },
    desc: {
      fontSize: '14px', color: '#64748b', lineHeight: '1.5'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <button style={styles.backBtn} onClick={() => navigate(-1)}><ArrowLeft size={24} /></button>
        <h1 style={styles.title}>Notifications</h1>
      </div>

      <div style={styles.listContainer}>
        {list.map((item) => (
          <div 
            key={item.id} 
            style={styles.card}
            onClick={() => handleNotificationClick(item)}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
          >
            <div style={styles.iconBox(item.bgColor)}>
              {item.icon}
            </div>

            <div style={styles.content}>
              {/* TOP ROW: Event Name + Time */}
              <div style={styles.rowTop}>
                <span style={styles.eventName}>{item.eventName}</span>
                <span style={styles.time}>{item.time}</span>
              </div>
              
              {/* MIDDLE: Status (e.g., Registration Approved) */}
              <div style={styles.statusText}>{item.status}</div>
              
              {/* BOTTOM: Description */}
              <div style={styles.desc}>{item.desc}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Notifications;
