import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, ArrowLeft, ArrowRight, AlertCircle, CheckCircle, Briefcase } from 'lucide-react';

const TeacherSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1); 
  const [showPass, setShowPass] = useState(false);
  const [errors, setErrors] = useState({});

  // RESPONSIVE CHECK
  const [isMobile, setIsMobile] = useState(window.innerWidth < 900);
  
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 900);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    collegeName: '',
    empId: '', // Teacher specific
    department: '',
    designation: '', // Teacher specific
    password: '',
    confirmPassword: ''
  });

  const styles = {
    pageContainer: {
      height: '100vh', width: '100vw', display: 'flex', alignItems: 'center', justifyContent: 'center',
      fontFamily: "'Inter', sans-serif", backgroundColor: '#f3f4f6', overflow: 'hidden'
    },
    mainWrapper: {
      display: 'flex', 
      width: isMobile ? '90vw' : '75vw', 
      height: isMobile ? 'auto' : '85vh', 
      minHeight: isMobile ? 'auto' : '650px',
      maxHeight: '95vh',
      backgroundColor: '#fff', 
      borderRadius: isMobile ? '4vw' : '2vw', 
      overflow: 'hidden',
      boxShadow: '0 2vh 6vh -1vh rgba(0, 0, 0, 0.15)', 
      flexDirection: 'row'
    },
    // Left Panel (Darker Purple for Teachers)
    leftPanel: {
      flex: 1, 
      background: 'linear-gradient(135deg, #4f46e5 0%, #4338ca 100%)', 
      display: isMobile ? 'none' : 'flex', 
      flexDirection: 'column', alignItems: 'center', justifyContent: 'center',
      color: '#fff', padding: '4vw', textAlign: 'center'
    },
    glassCard: {
      backgroundColor: 'rgba(255, 255, 255, 0.1)', backdropFilter: 'blur(10px)', 
      borderRadius: '1.5vw', padding: '1.5vw', 
      border: '1px solid rgba(255, 255, 255, 0.2)', marginTop: '2vh', maxWidth: '22vw'
    },
    heroImage: { width: '100%', borderRadius: '1vw', objectFit: 'cover' },
    
    // Right Panel
    rightPanel: {
      flex: 1.2, padding: isMobile ? '6vw' : '3vw 5vw', display: 'flex', flexDirection: 'column',
      justifyContent: 'center', backgroundColor: '#fff', position: 'relative'
    },
    header: { textAlign: 'center', marginBottom: '3vh' },
    stepIndicator: { fontSize: isMobile ? '3vw' : '0.8vw', color: '#4f46e5', fontWeight: '600', textTransform: 'uppercase', letterSpacing: '1px' },
    title: { fontSize: isMobile ? '6vw' : '2.2vw', fontWeight: '800', color: '#1f2937', marginTop: '0.5vh', marginBottom: '1vh' },
    
    inputGroup: { display: 'flex', flexDirection: 'column', gap: '0.8vh', marginBottom: '2vh' },
    label: { fontSize: isMobile ? '3.5vw' : '0.9vw', fontWeight: '700', color: '#374151', marginLeft: '0.2vw' },
    input: (hasError) => ({
      width: '100%', padding: isMobile ? '1.5vh 3vw' : '1.2vh 1vw', borderRadius: isMobile ? '2vw' : '0.8vw', 
      border: hasError ? '2px solid #ef4444' : '2px solid #e5e7eb', backgroundColor: '#fff', 
      fontSize: isMobile ? '4vw' : '1vw', outline: 'none', color: '#111827', transition: 'border-color 0.2s', boxSizing: 'border-box'
    }),
    
    splitRow: { display: 'flex', gap: isMobile ? '3vw' : '1.5vw', marginBottom: '2vh' },
    splitCol: { flex: 1, display: 'flex', flexDirection: 'column', gap: '0.8vh' },
    errorText: { fontSize: isMobile ? '3vw' : '0.8vw', color: '#ef4444', display: 'flex', alignItems: 'center', gap: '0.5vw', marginTop: '0.2vh' },
    
    actionBtn: {
      width: '100%', padding: isMobile ? '2vh' : '1.5vh', borderRadius: isMobile ? '2vw' : '0.8vw', border: 'none',
      background: '#4f46e5', color: '#fff', fontSize: isMobile ? '4vw' : '1.1vw', fontWeight: 'bold',
      cursor: 'pointer', marginTop: '1vh', boxShadow: '0 0.5vh 1.5vh rgba(79, 70, 229, 0.3)',
      display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '0.5vw', transition: 'transform 0.1s'
    },
    backBtn: {
      background: 'none', border: 'none', color: '#6b7280', fontSize: isMobile ? '3.5vw' : '1vw', fontWeight: '600',
      cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '0.5vw', marginBottom: '2vh', alignSelf: 'flex-start'
    },
    linkText: { textAlign: 'center', marginTop: '3vh', fontSize: isMobile ? '3.5vw' : '1vw', color: '#6b7280', paddingBottom: '1vh' }
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
    if(errors[e.target.name]) setErrors({...errors, [e.target.name]: null});
  };

  const validateStep1 = () => {
    let newErrors = {};
    if(!formData.fullName.trim()) newErrors.fullName = "Full name is required";
    if(!formData.email.trim()) newErrors.email = "Email is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep2 = () => {
    let newErrors = {};
    if(!formData.collegeName.trim()) newErrors.collegeName = "College name is required";
    if(!formData.empId.trim()) newErrors.empId = "Employee ID is required";
    if(!formData.department || formData.department === "Select") newErrors.department = "Select Department";
    if(!formData.designation || formData.designation === "Select") newErrors.designation = "Select Designation";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep3 = () => {
    let newErrors = {};
    if(formData.password.length < 6) newErrors.password = "Min 6 characters";
    if(formData.password !== formData.confirmPassword) newErrors.confirmPassword = "Passwords do not match";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => { 
    if(step === 1 && validateStep1()) setStep(2);
    else if (step === 2 && validateStep2()) setStep(3);
  };

  const handleSubmit = () => {
    if(validateStep3()) {
      onLogin({ 
        name: formData.fullName,
        email: formData.email,
        college: formData.collegeName,
        role: 'teacher',
        department: formData.department,
        regNo: formData.empId, // Using regNo field for Emp ID
        profileImage: 'https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=200' 
      });
      navigate('/');
    }
  };
  
  return (
    <div style={styles.pageContainer}>
      <div style={styles.mainWrapper}>
        
        {/* Left Panel */}
        <div style={styles.leftPanel}>
          <div style={{fontSize:'3vw', marginBottom:'1vh'}}>ðŸŽ“</div>
          <h1 style={{fontSize:'2.5vw', fontWeight:'800', marginBottom:'1vh'}}>Faculty Portal</h1>
          <p style={{fontSize:'1.1vw', opacity:0.9, marginBottom:'3vh'}}>Manage campus events and student approvals.</p>
          <div style={styles.glassCard}>
            <img src="https://images.unsplash.com/photo-1524178232363-1fb2b075b655?w=400&q=80" alt="Teacher" style={styles.heroImage} />
          </div>
        </div>

        {/* Right Panel */}
        <div style={styles.rightPanel}>
          {step > 1 && (
            <button style={styles.backBtn} onClick={() => setStep(step-1)}>
              <ArrowLeft size={isMobile ? 16 : 14} /> Back
            </button>
          )}
          
          <div style={styles.header}>
            <div style={styles.stepIndicator}>Step {step} of 3</div>
            <h2 style={styles.title}>{step === 1 ? "Teacher Details" : step === 2 ? "Professional Info" : "Secure Account"}</h2>
          </div>
          
          <div style={{animation: 'fadeIn 0.3s ease-in-out'}}>
            {step === 1 && (
              <>
                <div style={styles.inputGroup}>
                  <label style={styles.label}>Full Name</label>
                  <input style={styles.input(errors.fullName)} placeholder="Dr. Sarah Smith" name="fullName" value={formData.fullName} onChange={handleChange} autoFocus />
                  {errors.fullName && <div style={styles.errorText}><AlertCircle size={12}/> {errors.fullName}</div>}
                </div>
                <div style={styles.inputGroup}>
                  <label style={styles.label}>Official Email</label>
                  <input style={styles.input(errors.email)} placeholder="sarah@college.edu" name="email" value={formData.email} onChange={handleChange} />
                  {errors.email && <div style={styles.errorText}><AlertCircle size={12}/> {errors.email}</div>}
                </div>
                <button style={styles.actionBtn} onClick={handleNext}>Continue <ArrowRight size={18} /></button>
              </>
            )}

            {step === 2 && (
              <>
                <div style={styles.inputGroup}>
                  <label style={styles.label}>College Name</label>
                  <input style={styles.input(errors.collegeName)} placeholder="Engineering Tech Institute" name="collegeName" value={formData.collegeName} onChange={handleChange} />
                </div>
                
                <div style={styles.splitRow}>
                  <div style={styles.splitCol}>
                    <label style={styles.label}>Employee ID</label>
                    <input style={styles.input(errors.empId)} placeholder="FAC-992" name="empId" value={formData.empId} onChange={handleChange} />
                  </div>
                  <div style={styles.splitCol}>
                    <label style={styles.label}>Dept</label>
                    <select style={styles.input(errors.department)} name="department" value={formData.department} onChange={handleChange}>
                      <option>Select</option><option>CSE</option><option>ECE</option><option>EEE</option><option>Other</option>
                    </select>
                  </div>
                </div>

                <div style={styles.inputGroup}>
                  <label style={styles.label}>Designation</label>
                  <select style={styles.input(errors.designation)} name="designation" value={formData.designation} onChange={handleChange}>
                    <option>Select</option>
                    <option>Assistant Professor</option>
                    <option>Associate Professor</option>
                    <option>HOD</option>
                    <option>Lab Admin</option>
                  </select>
                  {errors.designation && <div style={styles.errorText}><AlertCircle size={12}/> {errors.designation}</div>}
                </div>

                <button style={styles.actionBtn} onClick={handleNext}>Continue <ArrowRight size={18} /></button>
              </>
            )}

            {step === 3 && (
              <>
                <div style={styles.inputGroup}>
                  <label style={styles.label}>Password</label>
                  <div style={{position:'relative'}}>
                     <input style={styles.input(errors.password)} placeholder="â€¢â€¢â€¢â€¢â€¢â€¢" name="password" type={showPass ? "text" : "password"} value={formData.password} onChange={handleChange} />
                     <div style={{position:'absolute', right: isMobile ? '3vw' : '1vw', top: isMobile ? '1.5vh' : '1.2vh', cursor:'pointer', color:'#9ca3af'}} onClick={() => setShowPass(!showPass)}>
                       {showPass ? <EyeOff size={20}/> : <Eye size={20}/>}
                     </div>
                  </div>
                  {errors.password && <div style={styles.errorText}><AlertCircle size={12}/> {errors.password}</div>}
                </div>
                <div style={styles.inputGroup}>
                  <label style={styles.label}>Confirm Password</label>
                  <input style={styles.input(errors.confirmPassword)} placeholder="â€¢â€¢â€¢â€¢â€¢â€¢" name="confirmPassword" type="password" value={formData.confirmPassword} onChange={handleChange} />
                </div>
                <div style={{display:'flex', gap: '0.5vw', alignItems:'center', marginTop:'1vh', marginBottom:'2vh'}}>
                  <input type="checkbox" style={{accentColor:'#4f46e5'}} />
                  <span style={{fontSize: isMobile ? '3vw' : '0.85vw', color:'#6b7280'}}>I verify I am a faculty member</span>
                </div>
                <button style={{...styles.actionBtn, background:'#10b981'}} onClick={handleSubmit}>
                  Create Faculty Account <CheckCircle size={18} />
                </button>
              </>
            )}
          </div>
          
          <div style={styles.linkText}>
            Already have an account? <Link to="/signin" style={{color: '#4f46e5', fontWeight:'bold', textDecoration:'none'}}>Login</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TeacherSignUp;
