import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Notifications from './pages/Notifications';
import Profile from './pages/Profile';
import BottomNav from './components/BottomNav';

function App() {
  const [userName] = useState("Priya Sharma"); // Syncs with Profile
  const [searchTerm, setSearchTerm] = useState("");

  const [allEvents] = useState([
    { 
      id: 1, title: "Campus Tech Summit 2024", date: "Mar 18 路 10:00 AM", 
      location: "Main Auditorium", category: "Tech", 
      image: "https://images.unsplash.com/photo-1504384308090-c894fdcc538d?w=800" 
    },
    { 
      id: 2, title: "Annual Cultural Night", date: "Mar 22 路 6:30 PM", 
      location: "Open Air Theatre", category: "Cultural", 
      image: "https://images.unsplash.com/photo-1492684223066-81342ee5ff30?w=800" 
    }
  ]);

  const [registeredList] = useState([
    { id: 101, name: "Spring Tech Symposium", date: "Mar 18, 2024", venue: "Main Campus", ticketId: "ST-1842" }
  ]);

  const filteredEvents = allEvents.filter(event => 
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Router>
      <div style={{ maxWidth: '375px', margin: '0 auto', backgroundColor: '#fff', minHeight: '100vh', position: 'relative' }}>
        <Routes>
          <Route path="/" element={<Dashboard userName={userName} events={filteredEvents} searchTerm={searchTerm} setSearchTerm={setSearchTerm} regCount={registeredList.length} />} />
          <Route path="/events" element={<Events events={filteredEvents} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />} />
          <Route path="/my-events" element={<MyEvents registeredEvents={registeredList} />} />
          <Route path="/notifications" element={<Notifications />} />
          <Route path="/profile" element={<Profile userName={userName} />} />
        </Routes>
        <BottomNav />
      </div>
    </Router>
  );
}

export default App;
