import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// 1. ADD THESE IMPORTS:
import Dashboard from './pages/Dashboard';
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import ThemeSelection from './pages/ThemeSelection';
import Settings from './pages/Settings';
import BottomNav from './components/BottomNav';

function App() {
  const [theme, setTheme] = useState('light');
  const [searchTerm, setSearchTerm] = useState("");
  const [user, setUser] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2023-4589"
  });

  // 2. DEFINE THESE VARIABLES:
  const [allEvents] = useState([
    { id: 1, title: "Campus Tech Summit", date: "Feb 15", location: "Auditorium", image: "https://via.placeholder.com/300" }
  ]);
  const [registeredList] = useState([]);
  const filteredEvents = allEvents.filter(e => e.title.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <Router>
      <div style={{ maxWidth: '375px', margin: '0 auto', backgroundColor: theme === 'dark' ? '#0f172a' : '#fff', minHeight: '100vh' }}>
        <Routes>
          <Route path="/" element={<Dashboard user={user} events={filteredEvents} searchTerm={searchTerm} setSearchTerm={setSearchTerm} regCount={registeredList.length} theme={theme} />} />
          <Route path="/profile" element={<Profile user={user} theme={theme} />} />
          <Route path="/settings" element={<Settings theme={theme} />} />
          <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
          <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} />} />
          <Route path="/change-password" element={<ChangePassword />} />
        </Routes>
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
