import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import Notifications from './pages/Notifications';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';

function App() {
  // We store the list of registered events here so both pages can see it
  const [registeredEvents, setRegisteredEvents] = useState([
    { id: 1, name: "Spring Tech Symposium", date: "Mar 18, 2024", venue: "Main Campus" },
    { id: 2, name: "Design Thinking Workshop", date: "Mar 22, 2024", venue: "Creative Arts Studio" },
    { id: 3, name: "Annual Science Fair", date: "Apr 05, 2024", venue: "North Wing" }
  ]);

  return (
    <Router>
      <Routes>
        {/* Pass the length of the list to the Dashboard */}
        <Route path="/" element={<Dashboard regCount={registeredEvents.length} />} />

        {/* Pass the actual list to the MyEvents page */}
        <Route path="/my-events" element={<MyEvents events={registeredEvents} />} />

        <Route path="/notifications" element={<Notifications />} />
        <Route path="/events" element={<Events />} />
      </Routes>
    </Router>
  );
}

export default App;
