import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Sun, Moon, Monitor } from 'lucide-react';

const ThemeSelection = ({ currentTheme, onApply }) => {
  const navigate = useNavigate();
  const [selected, setSelected] = useState(currentTheme);
  const [showToast, setShowToast] = useState(false);

  const handleApply = () => {
    onApply(selected); // Changes theme globally in App.js
    setShowToast(true);
    setTimeout(() => setShowToast(false), 2000);
  };

  return (
    <div style={{ padding: '16px', backgroundColor: selected === 'dark' ? '#0f172a' : '#f8fafc', minHeight: '100vh' }}>
      <header style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <ArrowLeft onClick={() => navigate(-1)} style={{ cursor: 'pointer' }} />
        <h2 style={{ marginLeft: '15px', color: selected === 'dark' ? '#fff' : '#000' }}>App Theme</h2>
      </header>

      {/* Theme Options */}
      <div style={{ backgroundColor: selected === 'dark' ? '#1e293b' : '#fff', borderRadius: '16px', overflow: 'hidden' }}>
        {[
          { id: 'light', name: 'Light Mode', icon: <Sun /> },
          { id: 'dark', name: 'Dark Mode', icon: <Moon /> }
        ].map(item => (
          <div 
            key={item.id} 
            onClick={() => setSelected(item.id)}
            style={{ 
              display: 'flex', 
              padding: '16px', 
              alignItems: 'center', 
              borderBottom: '1px solid #334155',
              cursor: 'pointer'
            }}
          >
            <div style={{ color: '#6366f1', marginRight: '12px' }}>{item.icon}</div>
            <span style={{ flex: 1, color: selected === 'dark' ? '#fff' : '#1e293b' }}>{item.name}</span>
            <div style={{ 
              width: '20px', height: '20px', borderRadius: '50%', 
              border: `2px solid #6366f1`, display: 'flex', justifyContent: 'center', alignItems: 'center' 
            }}>
              {selected === item.id && <div style={{ width: '10px', height: '10px', backgroundColor: '#6366f1', borderRadius: '50%' }} />}
            </div>
          </div>
        ))}
      </div>

      <button 
        onClick={handleApply}
        style={{ 
          width: '100%', padding: '16px', marginTop: '30px', 
          backgroundColor: '#6366f1', color: '#fff', borderRadius: '12px', 
          border: 'none', fontWeight: 'bold' 
        }}
      >
        Apply Theme
      </button>

      {showToast && (
        <div style={{ position: 'fixed', bottom: '100px', left: '50%', transform: 'translateX(-50%)', backgroundColor: '#dcfce7', color: '#166534', padding: '8px 20px', borderRadius: '20px' }}>
          âœ“ Theme updated
        </div>
      )}
    </div>
  );
};

export default ThemeSelection;
