import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Home, Calendar, Ticket, User } from 'lucide-react';

// Added { theme } prop to the component
const BottomNav = ({ theme }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isActive = (path) => location.pathname === path;

  // Dynamic colors based on the theme
  const navStyles = {
    backgroundColor: theme === 'dark' ? '#1e293b' : '#ffffff', //
    borderTop: theme === 'dark' ? '1px solid #334155' : '1px solid #f1f5f9',
    activeColor: '#2563eb', // Brand blue stays consistent
    inactiveColor: theme === 'dark' ? '#94a3b8' : '#64748b'
  };

  const navItems = [
    { icon: <Home size={22} />, label: 'Home', path: '/' },
    { icon: <Calendar size={22} />, label: 'Events', path: '/events' },
    { icon: <Ticket size={22} />, label: 'My Events', path: '/my-events' },
    { icon: <User size={22} />, label: 'Profile', path: '/profile' }
  ];

  return (
    <nav style={{ 
      position: 'fixed', 
      bottom: 0, 
      left: '50%', 
      transform: 'translateX(-50%)', 
      width: '100%', 
      maxWidth: '375px', 
      backgroundColor: navStyles.backgroundColor, 
      display: 'flex', 
      justifyContent: 'space-around', 
      padding: '12px 0', 
      borderTop: navStyles.borderTop,
      zIndex: 1000 
    }}>
      {navItems.map((item) => (
        <div 
          key={item.path}
          onClick={() => navigate(item.path)} 
          style={{ 
            color: isActive(item.path) ? navStyles.activeColor : navStyles.inactiveColor, 
            textAlign: 'center',
            cursor: 'pointer'
          }}
        >
          {item.icon}
          <div style={{ fontSize: '10px', marginTop: '4px' }}>{item.label}</div>
        </div>
      ))}
    </nav>
  );
};

export default BottomNav;
