import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Camera, ChevronLeft } from 'lucide-react';
import GradientHeader from '../components/GradientHeader';

const EditProfile = ({ user, setUser, theme }) => {
  const navigate = useNavigate();
  const fileInputRef = useRef(null);

  // Local state for the form
  const [name, setName] = useState(user.name);
  const [email, setEmail] = useState(user.email);
  const [previewImage, setPreviewImage] = useState(user.profileImage);

  const isDark = theme === 'dark';

  // 1. Handle Image Selection
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Create a temporary URL for the selected file
      const imageUrl = URL.createObjectURL(file);
      setPreviewImage(imageUrl);
    }
  };

  // 2. Trigger File Input click
  const triggerFileInput = () => {
    fileInputRef.current.click();
  };

  // 3. Save Changes to Global State
  const handleSave = () => {
    setUser({ 
      ...user, 
      name: name,
      email: email,
      profileImage: previewImage // <--- Updates the global image
    });
    navigate('/profile');
  };

  const styles = {
    container: { backgroundColor: isDark ? '#0f172a' : '#f8fafc', minHeight: '100%', fontFamily: 'sans-serif' },
    content: { padding: '24px', display: 'flex', flexDirection: 'column', alignItems: 'center' },
    // Image Section
    imageWrapper: { position: 'relative', marginBottom: '30px' },
    profileImg: {
      width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover',
      border: '4px solid #fff', boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
    },
    cameraBtn: {
      position: 'absolute', bottom: '0', right: '0',
      backgroundColor: '#5c5cfc', color: '#fff',
      width: '32px', height: '32px', borderRadius: '50%',
      display: 'flex', alignItems: 'center', justifyContent: 'center',
      cursor: 'pointer', border: '2px solid #fff'
    },
    changeText: { color: '#5c5cfc', fontWeight: '600', marginTop: '10px', fontSize: '14px', cursor: 'pointer' },
    
    // Form Inputs
    formGroup: { width: '100%', marginBottom: '20px' },
    label: { display: 'block', marginBottom: '8px', fontSize: '14px', fontWeight: '600', color: '#64748b' },
    input: {
      width: '100%', padding: '16px', borderRadius: '12px',
      border: '1px solid #e2e8f0', backgroundColor: isDark ? '#1e293b' : '#fff',
      color: isDark ? '#fff' : '#1e293b', fontSize: '15px', outline: 'none', boxSizing: 'border-box'
    },
    disabledInput: {
      width: '100%', padding: '16px', borderRadius: '12px',
      border: '1px solid #e2e8f0', backgroundColor: isDark ? '#334155' : '#f1f5f9',
      color: '#94a3b8', fontSize: '15px', outline: 'none', boxSizing: 'border-box'
    },
    saveBtn: {
      width: '100%', padding: '16px', borderRadius: '16px',
      backgroundColor: '#5c5cfc', color: '#fff', border: 'none',
      fontSize: '16px', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px'
    },
    cancelBtn: {
      background: 'none', border: 'none', color: '#94a3b8', 
      fontSize: '14px', marginTop: '15px', cursor: 'pointer'
    }
  };

  return (
    <div style={styles.container}>
      <GradientHeader title="Edit Profile" showBack={true} />
      
      <div style={styles.content}>
        {/* Hidden File Input */}
        <input 
          type="file" 
          ref={fileInputRef} 
          onChange={handleImageChange} 
          accept="image/*" 
          style={{ display: 'none' }} 
        />

        {/* Profile Image & Camera Button */}
        <div style={styles.imageWrapper} onClick={triggerFileInput}>
          <img src={previewImage} alt="Profile" style={styles.profileImg} />
          <div style={styles.cameraBtn}>
            <Camera size={16} />
          </div>
        </div>
        <div style={styles.changeText} onClick={triggerFileInput}>Change Profile Photo</div>

        {/* Form Fields */}
        <div style={{ width: '100%', marginTop: '20px' }}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Full Name</label>
            <input style={styles.input} value={name} onChange={(e) => setName(e.target.value)} />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>College Name</label>
            <input style={styles.disabledInput} value={user.college} disabled />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Register Number</label>
            <input style={styles.disabledInput} value={user.regNo} disabled />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Email Address</label>
            <input style={styles.input} value={email} onChange={(e) => setEmail(e.target.value)} />
          </div>

          <button style={styles.saveBtn} onClick={handleSave}>Save Changes</button>
          <button style={styles.cancelBtn} onClick={() => navigate('/profile')}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
