import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import './App.css'; 

// Components
import Sidebar from './components/Sidebar'; 
import BottomNav from './components/BottomNav';
import LandingPage from './components/LandingPage'; 
import StudentSignIn from './components/StudentSignIn';
import StudentSignUp from './components/StudentSignUp'; // The generic signup with toggle
import TeacherSignUp from './components/TeacherSignUp'; // The specific teacher signup page

// Pages
import Dashboard from './pages/Dashboard';        // ✅ STUDENT DASHBOARD
import TeacherDashboard from './pages/TeacherDashboard'; // ✅ TEACHER DASHBOARD
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import ThemeSelection from './pages/ThemeSelection';
import EventDetails from './pages/EventDetails';
import Notifications from './pages/Notifications';
import TicketConfirmation from './pages/TicketConfirmation'; 
import PrivacyPolicy from './pages/PrivacyPolicy';
import HelpCenter from './pages/HelpCenter'; 

const AppContent = ({ 
  isAuthenticated, handleLogin, handleLogout, 
  theme, toggleTheme, setTheme, user, setUser, 
  allEvents, handleRegister, handleCancel, 
  searchTerm, setSearchTerm, registeredEvents, filteredEvents, 
  notifications, unreadCount, markNotificationsRead
}) => {
  const location = useLocation();
  const isDark = theme === 'dark';
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Theme Logic
  React.useEffect(() => {
    document.body.className = isDark ? 'dark-mode' : 'light-mode';
    document.body.style.backgroundColor = isDark ? '#0f172a' : '#f8fafc';
    document.body.style.margin = '0';
  }, [isDark]);

  // ✅ CHECK ROLE
  const isTeacher = user.role === 'teacher';

  const showNavOn = ['/', '/events', '/my-events', '/profile', '/dashboard'];
  const shouldShowNav = showNavOn.includes(location.pathname) && isAuthenticated;

  // 1. IF NOT LOGGED IN
  if (!isAuthenticated) {
    return (
      <div style={{ minHeight: '100vh', backgroundColor: isDark ? '#0f172a' : '#fff' }}>
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/signin" element={<StudentSignIn onLogin={handleLogin} />} /> 
          <Route path="/signup" element={<StudentSignUp onLogin={handleLogin} />} />
          <Route path="/teacher-signup" element={<TeacherSignUp onLogin={handleLogin} />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    );
  }

  // 2. IF LOGGED IN
  return (
    <div className="app-layout" style={{ display: 'flex', minHeight: '100vh', width: '100%' }}>
      
      {!isMobile && (
        <Sidebar user={user} theme={theme} onLogout={handleLogout} />
      )}

      <div className="main-content" style={{ 
        flex: 1, 
        marginLeft: isMobile ? '0px' : '50px', 
        width: isMobile ? '100%' : 'calc(100% - 50px)', 
        backgroundColor: isDark ? '#0f172a' : '#f8fafc',
        minHeight: '100vh',
        transition: 'margin-left 0.3s ease' 
      }}>
        <Routes>
          
          {/* ✅ THE REDIRECT LOGIC IS HERE */}
          <Route path="/" element={
            isTeacher ? (
              // If Role is Teacher -> Show Teacher Dashboard
              <TeacherDashboard 
                 user={user} 
                 theme={theme} 
                 toggleTheme={toggleTheme} 
                 onLogout={handleLogout} 
              />
            ) : (
              // If Role is Student -> Show Student Dashboard
              <Dashboard 
                user={user} 
                events={allEvents} 
                theme={theme} 
                toggleTheme={toggleTheme} 
                regCount={registeredEvents.length} 
                onRegister={handleRegister} 
                unreadCount={unreadCount} 
                onReadNotifications={markNotificationsRead} 
              />
            )
          } />
          
          {/* Shared Routes */}
          <Route path="/events" element={<Events allEvents={filteredEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} onRegister={handleRegister} />} />
          <Route path="/my-events" element={<MyEvents theme={theme} events={registeredEvents} onCancel={handleCancel} />} />
          <Route path="/profile" element={<Profile user={user} theme={theme} onLogout={handleLogout} />} />
          <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} theme={theme} />} />
          <Route path="/change-password" element={<ChangePassword theme={theme} />} />
          <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
          <Route path="/event-details/:id" element={<EventDetails allEvents={allEvents} theme={theme} onRegister={handleRegister} />} />
          <Route path="/notifications" element={<Notifications theme={theme} notificationsList={notifications} />} />
          <Route path="/ticket-confirmation/:id" element={<TicketConfirmation allEvents={allEvents} theme={theme} onCancel={handleCancel} />} />
          <Route path="/privacy" element={<PrivacyPolicy theme={theme} />} />
          <Route path="/help" element={<HelpCenter theme={theme} />} />

          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
        
        <div style={{ height: '80px' }} className="mobile-only-spacer"></div>
      </div>

      {shouldShowNav && isMobile && (
        <BottomNav theme={theme} />
      )}
    </div>
  );
};

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  
  // Default User
  const [user, setUser] = useState({
    name: "Guest",
    email: "",
    role: "student", // Default role
    profileImage: "https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=200" 
  });

  const toggleTheme = () => setTheme((prev) => (prev === 'light' ? 'dark' : 'light'));

  const handleLogin = (userData) => { 
    if (userData) setUser(prev => ({ ...prev, ...userData })); 
    setIsAuthenticated(true); 
  };

  const handleLogout = () => { 
    setIsAuthenticated(false); 
    setUser({ name: "Guest", email: "", role: "student", profileImage: "" }); 
  };

  // ... (Keep existing Mock Data and Helper functions for events/notifications)
  const [allEvents, setAllEvents] = useState([]); // (Populate with your existing mock data)
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const markNotificationsRead = () => setUnreadCount(0);
  const handleRegister = (eventId) => {}; // (Your logic)
  const handleCancel = (eventId) => {}; // (Your logic)
  const filteredEvents = allEvents; // (Your logic)
  const registeredEvents = []; // (Your logic)

  return (
    <Router>
      <AppContent 
        isAuthenticated={isAuthenticated} handleLogin={handleLogin} handleLogout={handleLogout} 
        theme={theme} setTheme={setTheme} toggleTheme={toggleTheme} 
        user={user} setUser={setUser} 
        allEvents={allEvents} handleRegister={handleRegister} handleCancel={handleCancel} 
        searchTerm={searchTerm} setSearchTerm={setSearchTerm} 
        registeredEvents={registeredEvents} filteredEvents={filteredEvents} 
        notifications={notifications} unreadCount={unreadCount} markNotificationsRead={markNotificationsRead} 
      />
    </Router>
  );
}

export default App;
