import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react'; 
import { ArrowLeft } from 'lucide-react';

const TicketConfirmation = ({ allEvents, theme, onCancel }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const event = allEvents.find(e => e.id === parseInt(id));
  const isDark = theme === 'dark';

  if (!event) return null;

  const styles = {
    container: { maxWidth: '600px', margin: '0 auto', padding: '24px', fontFamily: 'sans-serif', display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '100vh', justifyContent: 'center' },
    
    // Ticket Style
    ticket: {
      width: '100%',
      backgroundColor: '#fff',
      borderRadius: '24px',
      padding: '30px',
      boxShadow: '0 10px 40px rgba(0,0,0,0.1)',
      textAlign: 'center',
      position: 'relative',
      color: '#1e293b'
    },
    // The "Cutout" circles
    cutoutLeft: { position: 'absolute', top: '70%', left: '-15px', width: '30px', height: '30px', borderRadius: '50%', backgroundColor: isDark ? '#0f172a' : '#f8fafc' },
    cutoutRight: { position: 'absolute', top: '70%', right: '-15px', width: '30px', height: '30px', borderRadius: '50%', backgroundColor: isDark ? '#0f172a' : '#f8fafc' },
    borderLine: { borderBottom: '2px dashed #cbd5e1', margin: '30px 0' },

    img: { width: '80px', height: '80px', borderRadius: '12px', objectFit: 'cover', margin: '0 auto 15px auto' },
    h2: { fontSize: '22px', fontWeight: 'bold', marginBottom: '5px' },
    p: { color: '#64748b', fontSize: '14px', marginBottom: '20px' },
    backBtn: { marginTop: '30px', background: 'none', border: 'none', color: '#64748b', cursor: 'pointer', fontSize: '14px' }
  };

  return (
    <div style={styles.container}>
      <div style={styles.ticket}>
        <img src={event.image} alt="" style={styles.img} />
        <h2 style={styles.h2}>{event.title}</h2>
        <p style={styles.p}>{event.date} • {event.location}</p>
        
        <div style={styles.borderLine}></div>
        {/* Cutouts */}
        <div style={styles.cutoutLeft}></div>
        <div style={styles.cutoutRight}></div>

        <div style={{ marginBottom: '10px' }}>
          <QRCodeSVG value={`TICKET-${event.id}`} size={160} />
        </div>
        <p style={{fontSize:'12px', color:'#94a3b8', textTransform:'uppercase', letterSpacing:'1px'}}>Scan at entry</p>
      </div>

      <button style={styles.backBtn} onClick={() => navigate('/')}>
        ← Back to Home
      </button>
    </div>
  );
};

export default TicketConfirmation;
