import React from 'react';

const PhoneFrame = ({ children }) => {
  const styles = {
    // The background behind the phone (Desktop view)
    wrapper: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#e2e8f0', // Light gray desktop background
      padding: '20px',
      boxSizing: 'border-box',
      fontFamily: 'sans-serif'
    },
    // The Phone Body
    phone: {
      width: '100%',
      maxWidth: '414px', // Standard large phone width
      height: '850px',   // Fixed height
      maxHeight: '90vh', // Responsive on smaller laptop screens
      backgroundColor: '#fff',
      borderRadius: '45px',
      // The "Bezel" (Frame border)
      boxShadow: '0 0 0 12px #2d3748, 0 20px 50px rgba(0,0,0,0.3)', 
      overflow: 'hidden', // Clips the app to the rounded corners
      position: 'relative',
      display: 'flex',
      flexDirection: 'column'
    },
    // The Notch at the top
    notch: {
      position: 'absolute',
      top: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '150px',
      height: '28px',
      backgroundColor: '#2d3748',
      borderBottomLeftRadius: '16px',
      borderBottomRightRadius: '16px',
      zIndex: 9999,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center'
    },
    // Speaker grill inside notch
    speaker: {
      width: '50px',
      height: '4px',
      backgroundColor: '#4a5568',
      borderRadius: '2px'
    },
    // The Scrollable Screen Area
    screen: {
      flex: 1,
      overflowY: 'auto', // Allows scrolling inside the phone
      overflowX: 'hidden',
      height: '100%',
      position: 'relative',
      backgroundColor: '#fff' // Default background
    }
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.phone}>
        {/* Notch */}
        <div style={styles.notch}>
          <div style={styles.speaker}></div>
        </div>

        {/* App Content */}
        <div style={styles.screen} className="hide-scrollbar">
          {children}
        </div>
      </div>

      {/* CSS to hide scrollbar for cleaner look */}
      <style>{`
        .hide-scrollbar::-webkit-scrollbar {
          display: none;
        }
        .hide-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
      `}</style>
    </div>
  );
};

export default PhoneFrame;
