import React from 'react';
import { Bell, Clock } from 'lucide-react'; 
import { useNavigate } from 'react-router-dom'; 
import GradientHeader from '../components/GradientHeader'; // <--- Import

const Dashboard = ({ user, events, theme, regCount, unreadCount, onReadNotifications, t }) => {
  const navigate = useNavigate(); 
  const isDark = theme === 'dark';

  const styles = {
    container: { 
      padding: 0, 
      backgroundColor: isDark ? '#0f172a' : '#f8fafc', 
      minHeight: '100vh', 
      maxWidth: '430px', 
      margin: '0 auto' 
    },
    content: { padding: '0 20px 20px 20px' },
    // Notification Icon Style
    notifBtn: {
      position: 'relative',
      cursor: 'pointer',
      backgroundColor: 'rgba(255,255,255,0.2)',
      padding: '8px',
      borderRadius: '12px'
    },
    sectionHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px', marginTop: '10px' },
    seeAllBtn: { background: 'none', border: 'none', color: '#2563eb', fontSize: '14px', fontWeight: 'bold', cursor: 'pointer' },
    eventCard: { backgroundColor: isDark ? '#1e293b' : '#fff', borderRadius: '20px', padding: '16px', marginBottom: '16px', boxShadow: '0 4px 6px rgba(0,0,0,0.02)' },
    regBtn: { width: '100%', backgroundColor: '#2563eb', color: '#fff', border: 'none', padding: '12px', borderRadius: '12px', fontWeight: 'bold', marginTop: '12px', cursor: 'pointer' },
    // Stats
    statsRow: { display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '15px', marginBottom: '30px' },
    statCard: { backgroundColor: isDark ? '#1e293b' : '#fff', padding: '16px', borderRadius: '20px', display: 'flex', flexDirection: 'column', gap: '5px', boxShadow: '0 2px 10px rgba(0,0,0,0.02)' }
  };

  const NotificationIcon = (
    <div style={styles.notifBtn} onClick={() => { onReadNotifications(); navigate('/notifications'); }}>
      <Bell size={22} color="#fff" />
      {unreadCount > 0 && (
        <span style={{ position: 'absolute', top: 8, right: 8, width: '8px', height: '8px', backgroundColor: '#ef4444', borderRadius: '50%', border: '2px solid #fff' }}></span>
      )}
    </div>
  );

  return (
    <div style={styles.container}>
      {/* USE NEW HEADER */}
      <GradientHeader 
        title={`Hi, ${user.name.split(' ')[0]} ðŸ‘‹`}
        subtitle="Explore college events"
        rightElement={NotificationIcon}
        showBack={false} 
      />

      <div style={styles.content}>
        <div style={styles.statsRow}>
          <div style={styles.statCard}>
            <div style={{ fontSize: '13px', color: '#64748b' }}>{t.upcoming}</div>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: isDark?'#fff':'#1e293b' }}>{events.length}</div>
          </div>
          <div style={styles.statCard}>
            <div style={{ fontSize: '13px', color: '#64748b' }}>{t.registered}</div>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: isDark?'#fff':'#1e293b' }}>{regCount}</div>
          </div>
        </div>

        <div style={styles.sectionHeader}>
          <h3 style={{ fontSize: '18px', margin: 0, color: isDark ? '#fff' : '#1e293b' }}>{t.events}</h3>
          <button style={styles.seeAllBtn} onClick={() => navigate('/events')}>{t.seeAll}</button>
        </div>
        
        {events.slice(0, 2).map(event => (
          <div key={event.id} style={styles.eventCard} onClick={() => navigate(event.status === 'approved' ? `/ticket-confirmation/${event.id}` : `/event-details/${event.id}`)}>
            <img src={event.image} alt={event.title} style={{ width: '100%', height: '160px', objectFit: 'cover', borderRadius: '14px', marginBottom: '12px' }} />
            <h4 style={{ margin: '0 0 8px 0', fontSize: '18px', color: isDark ? '#fff' : '#1e293b' }}>{event.title}</h4>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px', fontSize: '14px', color: '#64748b' }}>
              <Clock size={16} /> {event.date}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
