import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Camera } from 'lucide-react';

const EditProfile = ({ user, setUser, theme }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';
  
  const fileInputRef = useRef(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [previewUrl, setPreviewUrl] = useState(user?.profileImage || '');

  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Form state
  const [formData, setFormData] = useState({
    name: user?.name || '',
    college: user?.college || '',
    regNo: user?.regNo || '',
    email: user?.email || '',
  });

  // ✅ HANDLE FILE SELECTION - Upload immediately
  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setLoading(true);
    setMessage('');

    try {
      // Create FormData for upload
      const imageFormData = new FormData();
      imageFormData.append('profileImage', file);

      // Get token
      const userData = JSON.parse(localStorage.getItem('user'));
      
      // Upload to backend
      const response = await fetch('http://localhost:5000/api/auth/upload-profile-image', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${userData.token}`
        },
        body: imageFormData
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Upload failed');
      }

      // ✅ Update local state with new image path
      setPreviewUrl(data.profileImage);
      setMessage('✅ Image uploaded! Now save other changes.');

    } catch (error) {
      console.error('Upload error:', error);
      setMessage('❌ Error uploading image: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // ✅ SAVE ALL CHANGES (including image)
  const handleSave = async () => {
    setLoading(true);
    setMessage('');

    try {
      const userData = JSON.parse(localStorage.getItem('user'));

      // Call backend API to update ALL profile data
      const response = await fetch('http://localhost:5000/api/auth/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${userData.token}`
        },
        body: JSON.stringify({
          name: formData.name,
          college: formData.college,
          regNo: formData.regNo,
          profileImage: previewUrl // Use the uploaded image URL
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Save failed');
      }

      // ✅ Update localStorage with new data from MongoDB
      const updatedUserData = {
        ...userData,
        name: data.name,
        college: data.college,
        regNo: data.regNo,
        department: data.department,
        profileImage: data.profileImage,
        email: data.email,
        _id: data._id,
        role: data.role,
        token: userData.token
      };
      localStorage.setItem('user', JSON.stringify(updatedUserData));

      // ✅ Update global state
      setUser(updatedUserData);

      setMessage('✅ Profile saved successfully!');
      
      // Navigate after short delay
      setTimeout(() => {
        navigate('/profile');
      }, 1000);

    } catch (error) {
      console.error('Save error:', error);
      setMessage('❌ Error saving profile: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: {
      padding: isMobile ? '4vw' : '2vw',
      maxWidth: isMobile ? '100vw' : '50vw',
      margin: '0 auto',
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      minHeight: '100vh',
      fontFamily: "'Inter', sans-serif",
      color: isDark ? '#fff' : '#1e293b'
    },
    header: { display: 'flex', alignItems: 'center', gap: '2vw', marginBottom: '4vh' },
    backBtn: {
      background: 'none', border: 'none', cursor: 'pointer',
      color: isDark ? '#fff' : '#64748b', display: 'flex', alignItems: 'center'
    },
    pageTitle: { fontSize: isMobile ? '6vw' : '2vw', fontWeight: '800' },
    avatarContainer: {
      display: 'flex', flexDirection: 'column', alignItems: 'center', marginBottom: '4vh'
    },
    avatarWrapper: {
      position: 'relative', width: isMobile ? '25vw' : '8vw', height: isMobile ? '25vw' : '8vw',
    },
    avatar: {
      width: '100%', height: '100%', borderRadius: '50%', objectFit: 'cover',
      border: `4px solid ${isDark ? '#1e293b' : '#fff'}`,
      boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
    },
    cameraBtn: {
      position: 'absolute', bottom: '0', right: '0',
      backgroundColor: '#4f46e5', color: '#fff',
      width: isMobile ? '8vw' : '2.5vw', height: isMobile ? '8vw' : '2.5vw',
      borderRadius: '50%', border: `3px solid ${isDark ? '#0f172a' : '#fff'}`,
      display: 'flex', alignItems: 'center', justifyContent: 'center',
      cursor: 'pointer', boxShadow: '0 2px 5px rgba(0,0,0,0.2)'
    },
    changeText: {
      marginTop: '1vh', color: '#4f46e5', fontWeight: '600', fontSize: isMobile ? '3.5vw' : '0.9vw', cursor: 'pointer'
    },
    form: { display: 'flex', flexDirection: 'column', gap: '2.5vh' },
    label: { 
      fontSize: isMobile ? '3.5vw' : '0.9vw', fontWeight: '600', marginBottom: '0.8vh', 
      color: isDark ? '#cbd5e1' : '#475569' 
    },
    input: {
      width: '100%', padding: isMobile ? '1.5vh 3vw' : '1.2vh 1vw',
      borderRadius: isMobile ? '2vw' : '0.6vw',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      color: isDark ? '#fff' : '#1e293b',
      fontSize: isMobile ? '4vw' : '1vw',
      outline: 'none'
    },
    saveBtn: {
      marginTop: '3vh', width: '100%', padding: isMobile ? '2vh' : '1.5vh',
      backgroundColor: loading ? '#94a3b8' : '#4f46e5',
      color: '#fff', border: 'none', borderRadius: isMobile ? '2vw' : '0.8vw',
      fontSize: isMobile ? '4vw' : '1.1vw', fontWeight: 'bold',
      cursor: loading ? 'not-allowed' : 'pointer',
      display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '0.5vw'
    },
    message: {
      marginTop: '2vh', padding: '1vh',
      backgroundColor: message.includes('✅') ? '#dcfce7' : '#fee2e2',
      color: message.includes('✅') ? '#166534' : '#991b1b',
      borderRadius: '0.5vw', fontSize: isMobile ? '3.5vw' : '0.9vw', textAlign: 'center'
    }
  };

  // Get full image URL
  const getImageUrl = (url) => {
    if (!url) return 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=200&q=80';
    if (url.startsWith('http')) return url;
    if (url.startsWith('/uploads')) return `http://localhost:5000${url}`;
    return url;
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <button style={styles.backBtn} onClick={() => navigate(-1)}>
          <ArrowLeft size={isMobile ? 24 : 24} />
        </button>
        <h1 style={styles.pageTitle}>Edit Profile</h1>
      </div>

      {/* Profile Image */}
      <div style={styles.avatarContainer}>
        <div style={styles.avatarWrapper}>
          <img 
            src={getImageUrl(previewUrl)} 
            alt="Profile" 
            style={styles.avatar}
            onError={(e) => {
              e.target.src = 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=200&q=80';
            }}
          />
          <div 
            style={{...styles.cameraBtn, opacity: loading ? 0.7 : 1}}
            onClick={() => fileInputRef.current.click()}
          >
            <Camera size={isMobile ? 16 : 18} />
          </div>
        </div>
        <span style={styles.changeText} onClick={() => fileInputRef.current.click()}>
          {loading ? 'Uploading...' : 'Change Photo'}
        </span>
        <input 
          type="file" 
          ref={fileInputRef} 
          style={{ display: 'none' }} 
          accept="image/*"
          onChange={handleFileChange}
        />
      </div>

      {/* Message */}
      {message && <div style={styles.message}>{message}</div>}

      {/* Form */}
      <div style={styles.form}>
        <div>
          <label style={styles.label}>Full Name</label>
          <input 
            style={styles.input} 
            name="name" 
            value={formData.name} 
            onChange={(e) => setFormData({ ...formData, name: e.target.value })} 
          />
        </div>

        <div>
          <label style={styles.label}>College Name</label>
          <input 
            style={styles.input} 
            name="college" 
            value={formData.college} 
            onChange={(e) => setFormData({ ...formData, college: e.target.value })} 
          />
        </div>

        <div>
          <label style={styles.label}>Register Number</label>
          <input 
            style={styles.input} 
            name="regNo" 
            value={formData.regNo} 
            onChange={(e) => setFormData({ ...formData, regNo: e.target.value })} 
          />
        </div>

        <div>
          <label style={styles.label}>Email (cannot change)</label>
          <input 
            style={{...styles.input, opacity: 0.7}} 
            name="email" 
            value={formData.email} 
            disabled 
          />
        </div>

        <button 
          style={styles.saveBtn} 
          onClick={handleSave}
          disabled={loading}
        >
          {loading ? 'Saving...' : 'Save Changes'}
        </button>
      </div>
    </div>
  );
};

export default EditProfile;
