import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Clock, MapPin, Users } from 'lucide-react';

const EventDetails = ({ allEvents, theme, onRegister }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isDark = theme === 'dark';
  const event = allEvents.find(e => e.id === parseInt(id));

  if (!event) return null;

  return (
    <div style={{ backgroundColor: isDark ? '#0f172a' : '#fff', minHeight: '100vh' }}>
      {/* Header Image & Back Button */}
      <div style={{ position: 'relative', height: '300px' }}>
        <img src={event.image} alt={event.title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
        <div 
          onClick={() => navigate(-1)} 
          style={{ position: 'absolute', top: '20px', left: '20px', backgroundColor: '#fff', borderRadius: '50%', padding: '10px', cursor: 'pointer', boxShadow: '0 4px 10px rgba(0,0,0,0.2)' }}
        >
          <ArrowLeft size={24} color="#000" />
        </div>
      </div>

      {/* Content Section */}
      <div style={{ padding: '24px' }}>
        <h1 style={{ fontSize: '28px', fontWeight: 'bold', margin: '0 0 8px 0' }}>{event.title}</h1>
        <p style={{ color: '#5c5cfc', fontWeight: 'bold', fontSize: '16px', marginBottom: '20px' }}>{event.category}</p>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '16px', color: isDark ? '#cbd5e0' : '#1e293b' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}><Clock size={20} color="#5c5cfc"/> {event.date}</div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}><MapPin size={20} color="#5c5cfc"/> {event.location}</div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}><Users size={20} color="#5c5cfc"/> 45 Seats Left</div>
        </div>

        <div style={{ marginTop: '30px' }}>
          <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '12px' }}>About Event</h3>
          <p style={{ color: '#94a3b8', lineHeight: '1.6' }}>
            Join us for a deep dive into {event.title}. This session covers industry standards and hands-on practice.
          </p>
        </div>

        {/* REGISTRATION BUTTON: Only works if NOT registered */}
        <div style={{ marginTop: '40px' }}>
          {!event.isRegistered ? (
            <button 
              onClick={() => { onRegister(event.id); navigate('/my-events'); }}
              style={{ width: '100%', backgroundColor: '#5c5cfc', color: '#fff', border: 'none', padding: '16px', borderRadius: '16px', fontWeight: 'bold', fontSize: '16px' }}
            >
              Register Now
            </button>
          ) : (
            <div style={{ textAlign: 'center', color: '#16a34a', fontWeight: 'bold', padding: '16px', backgroundColor: '#f0fdf4', borderRadius: '16px' }}>
              âœ“ You are registered for this event
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
