import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import Profile from './pages/Profile';
import Settings from './pages/Settings';
import BottomNav from './components/BottomNav';

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [user] = useState({ name: "Priya Sharma", email: "priya@uni.edu", college: "Engineering Tech Institute", regNo: "ETI-2023" });

  const [allEvents] = useState([
    { id: 1, title: "Campus Tech Summit 2024", date: "Mar 18 路 10 AM", location: "Auditorium", category: "Tech", image: "https://via.placeholder.com/300" },
    { id: 2, title: "Cultural Night", date: "Mar 22 路 6 PM", location: "Open Theatre", category: "Cultural", image: "https://via.placeholder.com/300" }
  ]);

  const filteredEvents = allEvents.filter(event => event.title.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <Router>
      <div style={{ maxWidth: '375px', margin: '0 auto', backgroundColor: theme === 'dark' ? '#0f172a' : '#fff', minHeight: '100vh' }}>
        <Routes>
          <Route path="/" element={<Dashboard user={user} events={filteredEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />} />
          {/* Use 'allEvents' prop to match component */}
          <Route path="/events" element={<Events allEvents={filteredEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />} />
          <Route path="/profile" element={<Profile user={user} theme={theme} />} />
        </Routes>
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
