import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import BottomNav from './components/BottomNav';

function App() {
  // Centralized user data to sync across all screens
  const [user, setUser] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2023-4589"
  });

  return (
    <Router>
      <div style={{ maxWidth: '375px', margin: '0 auto', backgroundColor: '#f8fafc', minHeight: '100vh', position: 'relative' }}>
        <Routes>
          {/* Passing the name to Dashboard for the "Hi Priya" greeting */}
          <Route path="/" element={<Dashboard userName={user.name} />} />
          <Route path="/profile" element={<Profile user={user} />} />
          <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} />} />
        </Routes>
        <BottomNav />
      </div>
    </Router>
  );
}

export default App;
