import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, ArrowLeft, ArrowRight, AlertCircle, CheckCircle } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1); 

  // ✅ This state determines the role based on the toggle in your screenshot
  const [isStudent, setIsStudent] = useState(true); 

  const [showPass, setShowPass] = useState(false);
  const [errors, setErrors] = useState({});
  const [isMobile, setIsMobile] = useState(window.innerWidth < 900);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 900);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const [formData, setFormData] = useState({
    fullName: '', email: '', collegeName: '', rollNo: '', department: '', password: '', confirmPassword: ''
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
    if(errors[e.target.name]) setErrors({...errors, [e.target.name]: null});
  };

  // ... (Validation functions remain the same: validateStep1, validateStep2, validateStep3) ...
  const validateStep1 = () => {
    let newErrors = {};
    if(!formData.fullName.trim()) newErrors.fullName = "Full name is required";
    if(!formData.email.trim()) newErrors.email = "Email is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep2 = () => {
    let newErrors = {};
    if(!formData.collegeName.trim()) newErrors.collegeName = "College name is required";
    // Check Roll No or Emp ID based on selection
    if(!formData.rollNo.trim()) newErrors.rollNo = isStudent ? "Roll number is required" : "Employee ID is required";
    if(!formData.department || formData.department === "Select") newErrors.department = "Please select a department";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateStep3 = () => {
    let newErrors = {};
    if(formData.password.length < 6) newErrors.password = "Min 6 characters";
    if(formData.password !== formData.confirmPassword) newErrors.confirmPassword = "Passwords do not match";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => { 
    if(step === 1 && validateStep1()) setStep(2); 
    else if (step === 2 && validateStep2()) setStep(3);
  };

  const handleBack = () => { if(step > 1) setStep(step - 1); };

  // ✅ CRITICAL: This sends the correct role to App.js
  const handleSubmit = () => {
    if(validateStep3()) {
      onLogin({ 
        name: formData.fullName,
        email: formData.email,
        college: formData.collegeName,
        // ✅ Determines role based on the Toggle Button state
        role: isStudent ? 'student' : 'teacher', 
        department: formData.department,
        regNo: formData.rollNo,
        profileImage: 'https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=200'
      });
      // ✅ Redirects to Home (App.js decides which dashboard to show)
      navigate('/'); 
    }
  };

  // --- STYLES (Kept simplified for brevity, paste your previous styles here) ---
  const styles = {
    // ... (Use the same styles as before)
    pageContainer: { height: '100vh', width: '100vw', display: 'flex', alignItems: 'center', justifyContent: 'center', fontFamily: "'Inter', sans-serif", backgroundColor: '#f3f4f6', overflow: 'hidden' },
    mainWrapper: { display: 'flex', width: isMobile ? '90vw' : '75vw', height: isMobile ? 'auto' : '85vh', backgroundColor: '#fff', borderRadius: '24px', overflow: 'hidden', boxShadow: '0 20px 60px -10px rgba(0,0,0,0.15)', flexDirection: 'row' },
    leftPanel: { flex: 1, background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)', display: isMobile ? 'none' : 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', color: '#fff', padding: '40px', textAlign: 'center' },
    rightPanel: { flex: 1.2, padding: isMobile ? '30px' : '40px 60px', display: 'flex', flexDirection: 'column', justifyContent: 'center', backgroundColor: '#fff', position: 'relative' },
    input: { width: '100%', padding: '14px', borderRadius: '12px', border: '2px solid #e5e7eb', backgroundColor: '#fff', fontSize: '15px', outline: 'none', marginBottom: '10px' },
    toggleContainer: { display: 'flex', backgroundColor: '#f3f4f6', borderRadius: '10px', padding: '4px', marginBottom: '20px' },
    toggleBtn: (active) => ({ flex: 1, padding: '10px', borderRadius: '8px', border: 'none', fontSize: '13px', fontWeight: '600', cursor: 'pointer', backgroundColor: active ? '#6366f1' : 'transparent', color: active ? '#fff' : '#6b7280', transition: 'all 0.2s' }),
    actionBtn: { width: '100%', padding: '16px', borderRadius: '12px', border: 'none', background: '#6366f1', color: '#fff', fontSize: '16px', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px' }
  };

  return (
    <div style={styles.pageContainer}>
      <div style={styles.mainWrapper}>
        <div style={styles.leftPanel}>
           <h1 style={{fontSize:'32px', fontWeight:'800'}}>Join EventSphere</h1>
           <p>Manage events and registrations easily.</p>
        </div>

        <div style={styles.rightPanel}>
          {step > 1 && <button onClick={handleBack} style={{background:'none', border:'none', cursor:'pointer', marginBottom:'20px'}}>Back</button>}

          <h2 style={{fontSize:'26px', fontWeight:'800', marginBottom:'20px'}}>
             {step === 1 ? "Let's get started" : step === 2 ? "Academic Details" : "Set Password"}
          </h2>

          {step === 1 && (
             <>
               <input style={styles.input} placeholder="Full Name" name="fullName" value={formData.fullName} onChange={handleChange} />
               <input style={styles.input} placeholder="Email" name="email" value={formData.email} onChange={handleChange} />
               <button style={styles.actionBtn} onClick={handleNext}>Continue <ArrowRight size={18}/></button>
             </>
          )}

          {step === 2 && (
             <>
               {/* ✅ THE TOGGLE BUTTONS */}
               <div style={styles.toggleContainer}>
                 <button style={styles.toggleBtn(isStudent)} onClick={() => setIsStudent(true)}>Student</button>
                 <button style={styles.toggleBtn(!isStudent)} onClick={() => setIsStudent(false)}>Teacher</button>
               </div>

               <input style={styles.input} placeholder="College Name" name="collegeName" value={formData.collegeName} onChange={handleChange} />

               <div style={{display:'flex', gap:'10px'}}>
                 <div style={{flex:1}}>
                    {/* Dynamic Placeholder based on role */}
                   <input style={styles.input} placeholder={isStudent ? "Roll No (ID-2024)" : "Emp ID (EMP-01)"} name="rollNo" value={formData.rollNo} onChange={handleChange} />
                 </div>
                 <div style={{flex:1}}>
                   <select style={styles.input} name="department" value={formData.department} onChange={handleChange}>
                     <option>Select Dept</option><option>CSE</option><option>ECE</option><option>Other</option>
                   </select>
                 </div>
               </div>
               <button style={styles.actionBtn} onClick={handleNext}>Continue <ArrowRight size={18}/></button>
             </>
          )}

          {step === 3 && (
             <>
               <input style={styles.input} type="password" placeholder="Password" name="password" value={formData.password} onChange={handleChange} />
               <input style={styles.input} type="password" placeholder="Confirm Password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} />
               <button style={{...styles.actionBtn, backgroundColor: '#10b981'}} onClick={handleSubmit}>
                 Complete Registration <CheckCircle size={18}/>
               </button>
             </>
          )}

          <div style={{textAlign:'center', marginTop:'20px', fontSize:'14px'}}>
            Already have an account? <Link to="/signin" style={{color:'#6366f1', fontWeight:'bold'}}>Login</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentSignUp;
