import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Sun, Moon, Monitor } from 'lucide-react';

const ThemeSelection = () => {
  const navigate = useNavigate();
  const [selectedTheme, setSelectedTheme] = useState('light');
  const [showToast, setShowToast] = useState(false);

  const handleApply = () => {
    setShowToast(true);
    // Hide toast after 3 seconds
    setTimeout(() => setShowToast(false), 3000);
  };

  const themes = [
    { id: 'light', title: 'Light Mode', desc: 'Bright background with dark text', icon: <Sun size={20} /> },
    { id: 'dark', title: 'Dark Mode', desc: 'Dark background for low light', icon: <Moon size={20} /> },
    { id: 'system', title: 'System Default', desc: 'Match device appearance', icon: <Monitor size={20} /> }
  ];

  return (
    <div style={styles.container}>
      {/* Header */}
      <header style={styles.header}>
        <ArrowLeft size={24} onClick={() => navigate(-1)} style={{ cursor: 'pointer' }} />
        <h2 style={styles.headerTitle}>App Theme</h2>
        <div style={{ width: 24 }}></div>
      </header>

      <div style={styles.content}>
        {/* Selection List */}
        <div style={styles.card}>
          {themes.map((theme) => (
            <div 
              key={theme.id} 
              style={styles.optionRow} 
              onClick={() => setSelectedTheme(theme.id)}
            >
              <div style={styles.iconWrapper}>{theme.icon}</div>
              <div style={{ flex: 1 }}>
                <div style={styles.optionTitle}>{theme.title}</div>
                <div style={styles.optionDesc}>{theme.desc}</div>
              </div>
              <div style={styles.radioOuter}>
                {selectedTheme === theme.id && <div style={styles.radioInner} />}
              </div>
            </div>
          ))}
        </div>

        {/* Preview Area */}
        <div style={styles.previewContainer}>
          <div style={styles.previewHeader}>
            <span style={styles.previewLabel}>Preview</span>
            <span style={styles.previewStatusBadge}>Light Mode</span>
          </div>
          <div style={styles.previewBox}>
            <div style={styles.skeletonLineShort} />
            <div style={styles.skeletonLineLong} />
            <div style={styles.skeletonLineLong} />
            <div style={styles.previewButtons}>
              <div style={styles.skeletonBtn} />
              <div style={styles.skeletonBtn} />
            </div>
          </div>
        </div>

        {/* Action Button */}
        <button style={styles.applyBtn} onClick={handleApply}>
          Apply Theme
        </button>

        {/* Success Toast */}
        {showToast && (
          <div style={styles.toast}>
            <div style={styles.toastContent}>
              <span style={{ marginRight: '8px' }}>âœ“</span> Theme updated
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: { backgroundColor: '#f8fafc', minHeight: '100vh', fontFamily: 'sans-serif' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '16px', backgroundColor: '#fff', borderBottom: '1px solid #f1f5f9' },
  headerTitle: { fontSize: '18px', fontWeight: 'bold' },
  content: { padding: '16px', display: 'flex', flexDirection: 'column', gap: '20px' },
  card: { backgroundColor: '#fff', borderRadius: '16px', border: '1px solid #e2e8f0', overflow: 'hidden' },
  optionRow: { display: 'flex', alignItems: 'center', padding: '16px', borderBottom: '1px solid #f1f5f9', cursor: 'pointer' },
  iconWrapper: { color: '#6366f1', marginRight: '16px', backgroundColor: '#f0f4ff', padding: '8px', borderRadius: '10px' },
  optionTitle: { fontSize: '14px', fontWeight: 'bold', color: '#1e293b' },
  optionDesc: { fontSize: '12px', color: '#94a3b8' },
  radioOuter: { width: '20px', height: '20px', borderRadius: '50%', border: '2px solid #6366f1', display: 'flex', justifyContent: 'center', alignItems: 'center' },
  radioInner: { width: '10px', height: '100%', backgroundColor: '#6366f1', borderRadius: '50%' },
  previewContainer: { backgroundColor: '#fff', padding: '16px', borderRadius: '16px', border: '1px dotted #cbd5e1' },
  previewHeader: { display: 'flex', justifyContent: 'space-between', marginBottom: '12px' },
  previewLabel: { fontSize: '14px', color: '#94a3b8' },
  previewStatusBadge: { backgroundColor: '#f0f4ff', color: '#6366f1', fontSize: '10px', padding: '4px 8px', borderRadius: '8px', fontWeight: 'bold' },
  previewBox: { display: 'flex', flexDirection: 'column', gap: '8px' },
  skeletonLineShort: { height: '8px', width: '40%', backgroundColor: '#f1f5f9', borderRadius: '4px' },
  skeletonLineLong: { height: '8px', width: '100%', backgroundColor: '#f1f5f9', borderRadius: '4px' },
  previewButtons: { display: 'flex', gap: '10px', marginTop: '10px' },
  skeletonBtn: { height: '24px', flex: 1, backgroundColor: '#6366f1', borderRadius: '8px', opacity: 0.8 },
  applyBtn: { backgroundColor: '#6366f1', color: '#fff', border: 'none', padding: '16px', borderRadius: '12px', fontWeight: 'bold', cursor: 'pointer', marginTop: 'auto' },
  toast: { position: 'fixed', bottom: '100px', left: '50%', transform: 'translateX(-50%)', zIndex: 1000 },
  toastContent: { backgroundColor: '#dcfce7', color: '#166534', padding: '8px 20px', borderRadius: '20px', fontSize: '13px', border: '1px solid #bbf7d0', display: 'flex', alignItems: 'center' }
};

export default ThemeSelection;
