import React from 'react';

const Navbar = () => (
  <nav className="flex justify-between items-center px-6 lg:px-[10%] py-5 border-b-[5px] border-[#256d57] bg-white">
    {/* Logo */}
    <div className="font-bold text-xl flex items-center gap-2">
      <div className="bg-[#7e8aa5] text-white px-2 py-1 rounded font-['Tapestry']">
        ðŸŽ“
      </div>
      EventSphere
    </div>

    {/* Nav Links - Hidden on mobile, shown on large screens */}
    <div className="hidden md:flex gap-12 text-[#666] text-base font-medium">
      <span className="cursor-pointer hover:text-black">Home</span>
      <span className="cursor-pointer hover:text-black">Events</span>
      <span className="cursor-pointer hover:text-black">Workshops</span>
      <span className="cursor-pointer hover:text-black">About</span>
    </div>

    {/* Auth Buttons */}
    <div className="flex items-center gap-5">
      <span className="font-medium cursor-pointer hover:text-blue-600">Log in</span>
      <button className="bg-[#2563eb] text-white px-5 py-2.5 rounded-md font-semibold border-none hover:bg-blue-700 transition-colors">
        Sign up free
      </button>
    </div>
  </nav>
);

const Hero = () => {
  return (
    <div className="flex flex-col lg:flex-row items-center justify-between px-6 lg:px-[10%] py-20 gap-12 bg-gradient-to-br from-[#41794f] to-[#25354e] font-['Inter']">

      {/* Content Section */}
      <div className="flex-1 max-w-[600px] text-center lg:text-left">
        <span className="inline-flex items-center bg-[#8c9bc5] text-[#2563eb] px-4 py-1.5 rounded-full text-sm font-semibold mb-6">
          New Features Available
        </span>

        <h1 className="font-['Akaya_Telivigala'] text-[2.8rem] lg:text-[4rem] leading-[1.1] text-[#00bdc4] mb-6 font-extrabold">
          Discover Events Near You
        </h1>

        <p className="text-lg text-[#2161da] leading-relaxed mb-10">
          Join EventSphere to connect with people, discover workshops, and attend events that matter to you.
        </p>

        <div className="flex gap-4 justify-center lg:justify-start">
          <button className="bg-[#2563eb] text-white px-7 py-3.5 rounded-lg border border-[#221414] font-extrabold cursor-pointer hover:bg-blue-700 transition-colors">
            Get Started
          </button>
          <button className="bg-transparent text-white px-7 py-3.5 rounded-lg border border-white font-bold cursor-pointer hover:bg-white/10 transition-colors">
            Learn More
          </button>
        </div>
      </div>

      {/* Image Section */}
      <div className="flex-1 w-full flex justify-center lg:justify-end">
        <img 
          src="https://images.unsplash.com/photo-1501281668745-f7f57925c3b4?auto=format&fit=crop&w=620&q=80" 
          alt="Event Atmosphere" 
          className="w-full max-w-[620px] h-auto lg:h-[420px] rounded-[10px] shadow-[0_20px_40px_rgba(255,0,0,0.1)] object-cover"
        />
      </div>
    </div>
  );
};

function App() {
  return (
    <div className="min-h-screen">
      <Navbar />
      <Hero />
    </div>
  );
}

export default App;
