import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Camera } from 'lucide-react';

const EditProfile = ({ user, setUser, theme }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';
  const fileInputRef = useRef(null); // Ref for the hidden file input

  // Initialize form data with user's current info
  const [formData, setFormData] = useState({
    name: user.name,
    college: user.college,
    regNo: user.regNo,
    email: user.email,
    photo: "https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=400&q=80" // Default or user's current photo
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Triggered when the camera icon is clicked
  const handlePhotoClick = () => {
    fileInputRef.current.click();
  };

  // Triggered when a file is selected
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Create a URL for the selected file
      const photoUrl = URL.createObjectURL(file);
      // Update the form data with the new photo URL
      setFormData({ ...formData, photo: photoUrl });
    }
  };

  const handleSave = () => {
    // Update the global user state with the new form data
    setUser({ ...user, ...formData });
    // Navigate back to the profile page
    navigate('/profile');
  };

  const styles = {
    container: {
      padding: '20px',
      backgroundColor: isDark ? '#0f172a' : '#fff',
      minHeight: '100vh',
      maxWidth: '430px',
      margin: '0 auto',
      boxSizing: 'border-box',
      fontFamily: 'sans-serif'
    },
    header: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      marginBottom: '30px'
    },
    title: {
      fontSize: '20px',
      fontWeight: 'bold',
      color: isDark ? '#fff' : '#1e293b',
      margin: 0
    },
    photoContainer: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginBottom: '30px'
    },
    photoWrapper: {
      position: 'relative',
      width: '100px',
      height: '100px',
      marginBottom: '15px'
    },
    profilePhoto: {
      width: '100%',
      height: '100%',
      borderRadius: '50%',
      objectFit: 'cover',
      border: `3px solid ${isDark ? '#1e293b' : '#fff'}`,
      boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
    },
    cameraBtn: {
      position: 'absolute',
      bottom: '0',
      right: '0',
      backgroundColor: '#5c5cfc',
      color: '#fff',
      width: '32px',
      height: '32px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      border: `2px solid ${isDark ? '#0f172a' : '#fff'}`,
      cursor: 'pointer'
    },
    changePhotoText: {
      color: '#5c5cfc',
      fontWeight: 'bold',
      fontSize: '14px',
      cursor: 'pointer'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontSize: '14px',
      fontWeight: '600',
      color: '#94a3b8'
    },
    input: {
      width: '100%',
      padding: '16px',
      borderRadius: '12px',
      border: 'none',
      backgroundColor: isDark ? '#1e293b' : '#f8fafc',
      color: isDark ? '#fff' : '#1e293b',
      fontSize: '16px',
      boxSizing: 'border-box',
      outline: 'none'
    },
    saveBtn: {
      width: '100%',
      backgroundColor: '#5c5cfc',
      color: '#fff',
      padding: '16px',
      borderRadius: '16px',
      border: 'none',
      fontSize: '16px',
      fontWeight: 'bold',
      cursor: 'pointer',
      marginTop: '10px',
      marginBottom: '15px'
    },
    cancelBtn: {
      width: '100%',
      backgroundColor: 'transparent',
      color: '#94a3b8',
      border: 'none',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <ArrowLeft size={24} onClick={() => navigate(-1)} style={{ cursor: 'pointer', color: isDark ? '#fff' : '#1e293b' }} />
        <h2 style={styles.title}>Edit Profile</h2>
      </div>

      <div style={styles.photoContainer}>
        <div style={styles.photoWrapper}>
          <img src={formData.photo} alt="Profile" style={styles.profilePhoto} />
          {/* Camera Button triggers the file input */}
          <button style={styles.cameraBtn} onClick={handlePhotoClick}>
            <Camera size={18} />
          </button>
        </div>
        {/* Clicking this text also triggers the file input */}
        <span style={styles.changePhotoText} onClick={handlePhotoClick}>Change Profile Photo</span>
        
        {/* Hidden File Input */}
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileChange}
          style={{ display: 'none' }}
          accept="image/*"
        />
      </div>

      <div style={styles.formGroup}>
        <label style={styles.label}>Full Name</label>
        <input
          name="name"
          value={formData.name}
          onChange={handleChange}
          style={styles.input}
        />
      </div>

      <div style={styles.formGroup}>
        <label style={styles.label}>College Name</label>
        <input
          name="college"
          value={formData.college}
          onChange={handleChange}
          style={styles.input}
        />
      </div>

      <div style={styles.formGroup}>
        <label style={styles.label}>Register Number</label>
        <input
          name="regNo"
          value={formData.regNo}
          onChange={handleChange}
          style={styles.input}
        />
      </div>

      <div style={styles.formGroup}>
        <label style={styles.label}>Email Address</label>
        <input
          name="email"
          value={formData.email}
          onChange={handleChange}
          style={styles.input}
        />
      </div>

      <button style={styles.saveBtn} onClick={handleSave}>
        Save Changes
      </button>
      <button style={styles.cancelBtn} onClick={() => navigate(-1)}>
        Cancel
      </button>
    </div>
  );
};

export default EditProfile;
