import React, { useState } from 'react';
import { Ticket, MapPin, Calendar, Trash2, QrCode, AlertTriangle } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const MyEvents = ({ theme, events, onCancel }) => { 
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  // --- STATE FOR THE POPUP MODAL ---
  const [showModal, setShowModal] = useState(false);
  const [selectedEventId, setSelectedEventId] = useState(null);

  // Helper to find the event title for the popup message
  const selectedEvent = events.find(e => e.id === selectedEventId);

  // Function to open the popup
  const handleInitiateCancel = (eventId) => {
    setSelectedEventId(eventId);
    setShowModal(true);
  };

  // Function to actually cancel (Yes button)
  const handleConfirmCancel = () => {
    if (selectedEventId) {
      onCancel(selectedEventId);
      setShowModal(false);
      setSelectedEventId(null);
    }
  };

  const styles = {
    container: {
      padding: '20px',
      backgroundColor: isDark ? '#0f172a' : '#fff',
      minHeight: '100vh',
      paddingBottom: '120px', 
      boxSizing: 'border-box',
      position: 'relative' // Needed for overlay
    },
    title: {
      color: isDark ? '#fff' : '#1e293b',
      marginBottom: '25px',
      fontSize: '24px',
      fontWeight: 'bold'
    },
    card: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '20px',
      padding: '20px',
      marginBottom: '20px',
      border: '2px dashed #5c5cfc',
      position: 'relative',
      display: 'flex',
      flexDirection: 'column',
      gap: '15px',
      boxShadow: '0 4px 6px rgba(0,0,0,0.05)'
    },
    cardHeader: {
      display: 'flex',
      gap: '15px',
      alignItems: 'flex-start'
    },
    iconBox: {
      backgroundColor: '#5c5cfc',
      color: '#fff',
      padding: '12px',
      borderRadius: '12px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      height: 'fit-content'
    },
    eventTitle: {
      margin: '0 0 5px 0',
      color: isDark ? '#fff' : '#1e293b',
      fontSize: '18px',
      fontWeight: 'bold'
    },
    eventDate: {
      margin: 0,
      fontSize: '13px',
      color: '#94a3b8',
      display: 'flex',
      alignItems: 'center',
      gap: '5px',
      marginBottom: '4px'
    },
    eventLoc: {
      margin: 0,
      fontSize: '13px',
      color: '#5c5cfc',
      fontWeight: 'bold',
      display: 'flex',
      alignItems: 'center',
      gap: '5px'
    },
    actionButtons: {
      display: 'flex',
      gap: '10px',
      marginTop: '5px'
    },
    viewBtn: {
      flex: 1, 
      backgroundColor: '#5c5cfc',
      color: '#fff',
      border: 'none',
      padding: '12px',
      borderRadius: '12px',
      fontWeight: 'bold',
      fontSize: '14px',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '8px'
    },
    cancelBtn: {
      flex: 1, 
      backgroundColor: '#fee2e2',
      color: '#ef4444',
      border: 'none',
      padding: '12px',
      borderRadius: '12px',
      fontWeight: 'bold',
      fontSize: '14px',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '8px'
    },
    emptyState: {
      textAlign: 'center',
      marginTop: '100px',
      color: '#94a3b8'
    },
    browseBtn: {
      backgroundColor: '#5c5cfc',
      color: '#fff',
      border: 'none',
      padding: '12px 24px',
      borderRadius: '12px',
      marginTop: '15px',
      fontWeight: 'bold',
      cursor: 'pointer'
    },

    // --- NEW MODAL STYLES ---
    overlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      backgroundColor: 'rgba(0,0,0,0.6)',
      zIndex: 2000, // Very high z-index to sit on top of everything
      display: 'flex',
      alignItems: 'flex-end', // Aligns modal to bottom
      justifyContent: 'center'
    },
    modalContent: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      width: '100%',
      maxWidth: '430px',
      borderTopLeftRadius: '24px',
      borderTopRightRadius: '24px',
      padding: '30px 24px',
      textAlign: 'center',
      animation: 'slideUp 0.3s ease-out',
      boxShadow: '0 -10px 40px rgba(0,0,0,0.2)'
    },
    modalTitle: {
      fontSize: '20px',
      fontWeight: 'bold',
      color: isDark ? '#fff' : '#1e293b',
      marginBottom: '10px'
    },
    modalText: {
      fontSize: '14px',
      color: '#64748b',
      marginBottom: '24px',
      lineHeight: '1.5'
    },
    modalBtnDanger: {
      width: '100%',
      backgroundColor: '#fee2e2',
      color: '#ef4444',
      padding: '16px',
      borderRadius: '16px',
      border: 'none',
      fontSize: '16px',
      fontWeight: 'bold',
      cursor: 'pointer',
      marginBottom: '12px'
    },
    modalBtnCancel: {
      width: '100%',
      backgroundColor: isDark ? '#334155' : '#f1f5f9',
      color: isDark ? '#fff' : '#64748b',
      padding: '16px',
      borderRadius: '16px',
      border: 'none',
      fontSize: '16px',
      fontWeight: 'bold',
      cursor: 'pointer'
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>My Tickets</h2>

      {events.length > 0 ? (
        events.map(event => (
          <div key={event.id} style={styles.card}>
            <div style={styles.cardHeader}>
              <div style={styles.iconBox}>
                <Ticket size={24} />
              </div>
              <div style={{ flex: 1 }}>
                <h3 style={styles.eventTitle}>{event.title}</h3>
                <p style={styles.eventDate}><Calendar size={12} /> {event.date}</p>
                <p style={styles.eventLoc}><MapPin size={12} /> {event.location}</p>
              </div>
            </div>

            <div style={styles.actionButtons}>
              <button 
                style={styles.viewBtn} 
                onClick={() => navigate(`/ticket-confirmation/${event.id}`)}
              >
                <QrCode size={16} /> View Ticket
              </button>

              <button 
                style={styles.cancelBtn} 
                onClick={() => handleInitiateCancel(event.id)} // OPENS MODAL
              >
                <Trash2 size={16} /> Cancel
              </button>
            </div>
          </div>
        ))
      ) : (
        <div style={styles.emptyState}>
          <Ticket size={64} style={{ marginBottom: '15px', opacity: 0.3 }} />
          <p style={{ fontSize: '18px' }}>No tickets yet</p>
          <button onClick={() => navigate('/events')} style={styles.browseBtn}>
            Browse Events
          </button>
        </div>
      )}

      {/* --- CONFIRMATION POPUP SLIDE --- */}
      {showModal && (
        <div style={styles.overlay}>
          <div style={styles.modalContent}>
             <div style={{ width: '50px', height: '50px', backgroundColor: '#fee2e2', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto 15px auto' }}>
              <AlertTriangle size={24} color="#ef4444" />
            </div>
            
            <h3 style={styles.modalTitle}>Cancel Registration?</h3>
            <p style={styles.modalText}>
              Are you sure you want to cancel your ticket for <br/>
              <strong style={{ color: isDark ? '#fff' : '#000' }}>{selectedEvent?.title}</strong>?
              <br/>This action cannot be undone.
            </p>
            
            <button style={styles.modalBtnDanger} onClick={handleConfirmCancel}>
              Yes, Cancel It
            </button>
            <button style={styles.modalBtnCancel} onClick={() => setShowModal(false)}>
              No, Keep Ticket
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyEvents;
