import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Notifications from './pages/Notifications';
import Profile from './pages/Profile';
import BottomNav from './components/BottomNav';

function App() {
  const [userName] = useState("Priya Sharma"); //
  const [searchTerm, setSearchTerm] = useState("");

  // Shared data for Upcoming Events with real images
  const [allEvents] = useState([
    { 
      id: 1, 
      title: "Campus Tech Summit 2024", 
      date: "Mar 18 · 10:00 AM", 
      location: "Main Auditorium, Block A", 
      category: "Tech", 
      image: "https://images.unsplash.com/photo-1504384308090-c894fdcc538d?auto=format&fit=crop&w=800&q=80" 
    },
    { 
      id: 2, 
      title: "Annual Cultural Night", 
      date: "Mar 22 · 6:30 PM", 
      location: "Open Air Theatre", 
      category: "Cultural", 
      image: "https://images.unsplash.com/photo-1492684223066-81342ee5ff30?auto=format&fit=crop&w=800&q=80" 
    },
    { 
      id: 3, 
      title: "University Sports Day", 
      date: "Mar 30 · 8:00 AM", 
      location: "University Sports Ground", 
      category: "Sports", 
      image: "https://images.unsplash.com/photo-1526676037777-05a232554f77?auto=format&fit=crop&w=800&q=80" 
    }
  ]);

  // Shared data for Registered Events
  const [registeredList] = useState([
    { id: 101, name: "Spring Tech Symposium", date: "Mar 18, 2024", venue: "Main Campus Auditorium", ticketId: "ST-2024-1842" },
    { id: 102, name: "Design Thinking Workshop", date: "Mar 22, 2024", venue: "Creative Arts Studio B", ticketId: "DT-2024-9931" },
    { id: 103, name: "Annual Science Fair", date: "Apr 05, 2024", venue: "North Wing Exhibition Hall", ticketId: "SF-2024-0042" }
  ]);

  // Global search filtering logic
  const filteredEvents = allEvents.filter(event => 
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Router>
      <div style={{ maxWidth: '375px', margin: '0 auto', backgroundColor: '#f8fafc', minHeight: '100vh', position: 'relative' }}>
        <Routes>
          <Route path="/" element={
            <Dashboard 
              userName={userName}
              events={filteredEvents} 
              searchTerm={searchTerm}
              setSearchTerm={setSearchTerm}
              regCount={registeredList.length} 
            />
          } />
          <Route path="/events" element={
            <Events 
              events={filteredEvents} 
              searchTerm={searchTerm}
              setSearchTerm={setSearchTerm}
            />
          } />
          <Route path="/my-events" element={<MyEvents registeredEvents={registeredList} />} />
          <Route path="/notifications" element={<Notifications />} />
          <Route path="/profile" element={<Profile userName={userName} />} />
        </Routes>
        
        {/* Persistent Bottom Navigation */}
        <BottomNav />
      </div>
    </Router>
  );
}

export default App;
