import React from 'react';
import { Search, Bell, Clock, MapPin, Calendar, ArrowRight } from 'lucide-react'; 
import { useNavigate } from 'react-router-dom'; 

const Dashboard = ({ user, events, theme, regCount, unreadCount, onReadNotifications, t }) => {
  const navigate = useNavigate(); 
  const isDark = theme === 'dark';

  const styles = {
    // Main Container
    container: { 
      padding: '24px', 
      fontFamily: 'sans-serif',
      color: isDark ? '#fff' : '#1e293b',
      maxWidth: '1400px', // Prevent stretching on huge screens
      margin: '0 auto'
    },

    // --- TOP BAR (Search + Profile) ---
    topBar: {
      display: 'flex',
      gap: '20px',
      alignItems: 'center',
      marginBottom: '32px',
      flexWrap: 'wrap' // Allows wrapping on tiny screens
    },
    searchWrapper: {
      flex: 1,
      minWidth: '280px', // Ensures it doesn't get too small
      position: 'relative',
    },
    searchInput: {
      width: '100%',
      padding: '14px 20px 14px 50px',
      borderRadius: '30px',
      border: isDark ? '1px solid #334155' : '1px solid #cbd5e1',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      fontSize: '15px',
      outline: 'none',
      color: isDark ? '#fff' : '#1e293b',
      boxShadow: '0 2px 10px rgba(0,0,0,0.03)'
    },
    searchIcon: {
      position: 'absolute',
      left: '18px',
      top: '50%',
      transform: 'translateY(-50%)',
      color: '#94a3b8'
    },
    bellBtn: {
      position: 'relative',
      padding: '12px',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '50%',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },

    // --- HERO BANNER (Udemy Style) ---
    heroBanner: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: isDark ? '#312e81' : '#e0e7ff', // Dark Indigo vs Light Indigo
      borderRadius: '16px',
      padding: '40px',
      marginBottom: '40px',
      position: 'relative',
      overflow: 'hidden',
      boxShadow: '0 10px 30px rgba(0,0,0,0.05)'
    },
    heroContent: {
      maxWidth: '600px',
      zIndex: 2
    },
    heroTitle: {
      fontSize: '32px',
      fontWeight: '800',
      marginBottom: '16px',
      color: isDark ? '#fff' : '#1e1b4b',
      lineHeight: '1.2'
    },
    heroText: {
      fontSize: '18px',
      marginBottom: '24px',
      color: isDark ? '#c7d2fe' : '#4338ca',
      lineHeight: '1.5'
    },
    heroBtn: {
      backgroundColor: '#1e1b4b',
      color: '#fff',
      border: 'none',
      padding: '14px 32px',
      borderRadius: '8px',
      fontWeight: 'bold',
      fontSize: '16px',
      cursor: 'pointer'
    },
    // Decorative Image (Abstract Shapes or Student)
    heroImage: {
      height: '250px',
      objectFit: 'contain',
      display: 'block',
      // Hide image on mobile to save space
      '@media (max-width: 768px)': {
        display: 'none'
      }
    },

    // --- STATS ROW ---
    statsRow: {
      display: 'flex',
      gap: '20px',
      marginBottom: '40px',
      flexWrap: 'wrap'
    },
    statCard: {
      flex: 1,
      minWidth: '200px',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '24px',
      borderRadius: '16px',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      display: 'flex',
      alignItems: 'center',
      gap: '20px',
      boxShadow: '0 4px 6px rgba(0,0,0,0.02)'
    },
    statIconBox: (bg, color) => ({
      width: '50px', height: '50px', borderRadius: '12px',
      backgroundColor: bg, color: color,
      display: 'flex', alignItems: 'center', justifyContent: 'center'
    }),

    // --- EVENTS GRID ---
    sectionTitle: {
      fontSize: '24px',
      fontWeight: 'bold',
      marginBottom: '24px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    grid: {
      display: 'grid',
      // Auto-fit columns: at least 300px wide, fills remaining space
      gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
      gap: '24px'
    },
    eventCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '16px',
      overflow: 'hidden',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      transition: 'transform 0.2s, box-shadow 0.2s',
      cursor: 'pointer',
      display: 'flex',
      flexDirection: 'column'
    },
    cardImage: {
      width: '100%',
      height: '180px',
      objectFit: 'cover'
    },
    cardBody: {
      padding: '20px',
      flex: 1,
      display: 'flex',
      flexDirection: 'column'
    },
    cardTitle: {
      fontSize: '18px',
      fontWeight: 'bold',
      marginBottom: '10px',
      color: isDark ? '#fff' : '#1e293b'
    },
    cardMeta: {
      fontSize: '14px',
      color: '#64748b',
      marginBottom: '8px',
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    }
  };

  return (
    <div style={styles.container}>
      
      {/* 1. TOP BAR */}
      <div style={styles.topBar}>
        <div style={styles.searchWrapper}>
          <Search size={20} style={styles.searchIcon} />
          <input 
            style={styles.searchInput} 
            placeholder="Search for events, workshops..." 
          />
        </div>
        
        <div style={styles.bellBtn} onClick={() => { onReadNotifications(); navigate('/notifications'); }}>
          <Bell size={24} color={isDark ? '#fff' : '#64748b'} />
          {unreadCount > 0 && (
            <span style={{ position: 'absolute', top: 0, right: 0, width: '10px', height: '10px', backgroundColor: '#ef4444', borderRadius: '50%', border: '2px solid #fff' }}></span>
          )}
        </div>
        
        {/* Simple User Badge */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px', cursor: 'pointer' }} onClick={() => navigate('/profile')}>
           <img src={user.profileImage} alt="" style={{ width: 40, height: 40, borderRadius: '50%', objectFit: 'cover' }} />
           <span style={{ fontWeight: 'bold', display: window.innerWidth < 600 ? 'none' : 'block' }}>{user.name.split(' ')[0]}</span>
        </div>
      </div>

      {/* 2. HERO BANNER */}
      <div style={styles.heroBanner}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>Welcome back, {user.name.split(' ')[0]}!</h1>
          <p style={styles.heroText}>
            Ready to learn something new today? Check out the latest workshops happening on campus.
          </p>
          <button style={styles.heroBtn} onClick={() => navigate('/events')}>Explore Events</button>
        </div>
        
        {/* Responsive Image Hide Logic handled in CSS or simplified here */}
        <img 
          src="https://img.freepik.com/free-vector/happy-student-with-graduation-cap-diploma_23-2147954930.jpg?w=740" 
          alt="Student" 
          style={{ ...styles.heroImage, display: window.innerWidth < 768 ? 'none' : 'block' }} // Basic JS media query simulation
        />
      </div>

      {/* 3. STATS ROW */}
      <div style={styles.statsRow}>
        <div style={styles.statCard}>
          <div style={styles.statIconBox('#e0e7ff', '#4338ca')}><Calendar size={24} /></div>
          <div>
            <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{events.length}</div>
            <div style={{ color: '#64748b', fontSize: '14px' }}>Upcoming Events</div>
          </div>
        </div>
        <div style={styles.statCard}>
          <div style={styles.statIconBox('#dcfce7', '#15803d')}><Clock size={24} /></div>
          <div>
            <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{regCount}</div>
            <div style={{ color: '#64748b', fontSize: '14px' }}>Registered</div>
          </div>
        </div>
      </div>

      {/* 4. POPULAR EVENTS GRID */}
      <div>
        <div style={styles.sectionTitle}>
          <span>Popular Events</span>
          <div 
            style={{ fontSize: '14px', color: '#4f46e5', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '4px' }}
            onClick={() => navigate('/events')}
          >
            View all <ArrowRight size={16} />
          </div>
        </div>

        <div style={styles.grid}>
          {events.map(event => (
            <div 
              key={event.id} 
              style={styles.eventCard} 
              onClick={() => navigate(`/event-details/${event.id}`)}
              onMouseEnter={(e) => { e.currentTarget.style.transform = 'translateY(-5px)'; e.currentTarget.style.boxShadow = '0 10px 20px rgba(0,0,0,0.1)'; }}
              onMouseLeave={(e) => { e.currentTarget.style.transform = 'translateY(0)'; e.currentTarget.style.boxShadow = 'none'; }}
            >
              <img src={event.image} alt={event.title} style={styles.cardImage} />
              <div style={styles.cardBody}>
                <h3 style={styles.cardTitle}>{event.title}</h3>
                <div style={styles.cardMeta}><Calendar size={16} /> {event.date.split('·')[0]}</div>
                <div style={styles.cardMeta}><MapPin size={16} /> {event.location}</div>
                <div style={{ marginTop: 'auto', paddingTop: '15px' }}>
                  <span style={{ backgroundColor: '#f1f5f9', padding: '6px 12px', borderRadius: '20px', fontSize: '12px', fontWeight: '600', color: '#475569' }}>
                    {event.category}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

    </div>
  );
};

export default Dashboard;
