import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [isStudent, setIsStudent] = useState(true);
  const [showPass, setShowPass] = useState(false);

  // Track screen size for responsive layout
  const [isDesktop, setIsDesktop] = useState(window.innerWidth > 900);

  useEffect(() => {
    const handleResize = () => setIsDesktop(window.innerWidth > 900);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const [formData, setFormData] = useState({
    fullName: '',
    collegeName: '',
    rollNo: '',
    department: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const styles = {
    // 1. PAGE BACKGROUND (Clean Light Grey)
    pageContainer: {
      height: '100vh',
      width: '100vw',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: "'Inter', sans-serif",
      backgroundColor: '#f3f4f6', 
      overflow: 'hidden'
    },

    // 2. MAIN CARD (Floating Split Screen)
    mainWrapper: {
      display: 'flex',
      width: isDesktop ? '1000px' : '95%',
      height: isDesktop ? '650px' : 'auto', 
      maxHeight: '95vh',
      backgroundColor: '#fff',
      borderRadius: '30px', // Rounder corners like the image
      overflow: 'hidden',
      boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)', // Deep shadow
      flexDirection: 'row' 
    },

    // 3. LEFT PANEL (Info & Image - PURPLE)
    leftPanel: {
      flex: 1,
      background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)', // Vibrant Purple/Blue gradient
      display: isDesktop ? 'flex' : 'none',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff',
      padding: '40px',
      position: 'relative',
      textAlign: 'center'
    },

    // Decorative Circle behind image
    glassCard: {
      backgroundColor: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      borderRadius: '20px',
      padding: '20px',
      border: '1px solid rgba(255, 255, 255, 0.2)',
      marginTop: '20px',
      maxWidth: '320px'
    },

    heroImage: {
      width: '100%',
      height: 'auto',
      borderRadius: '10px',
      objectFit: 'cover'
    },

    welcomeTitle: { fontSize: '36px', fontWeight: '800', marginBottom: '10px', letterSpacing: '-0.5px' },
    welcomeText: { fontSize: '16px', opacity: 0.9, lineHeight: '1.5' },

    // 4. RIGHT PANEL (Form - WHITE)
    rightPanel: {
      flex: 1.2,
      padding: isDesktop ? '40px 50px' : '30px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      backgroundColor: '#fff',
      overflowY: 'auto' // Scrolls inside if needed
    },

    header: { textAlign: 'center', marginBottom: '20px' },
    title: { fontSize: '28px', fontWeight: '900', color: '#111827', marginBottom: '5px' },
    subtitle: { fontSize: '14px', color: '#6b7280' },

    // Toggle
    toggleContainer: {
      display: 'flex',
      backgroundColor: '#f3f4f6',
      borderRadius: '12px',
      padding: '5px',
      marginBottom: '20px'
    },
    toggleBtn: (active) => ({
      flex: 1, padding: '10px', borderRadius: '8px', border: 'none',
      fontSize: '14px', fontWeight: '600', cursor: 'pointer',
      backgroundColor: active ? '#6366f1' : 'transparent', // Purple match
      color: active ? '#fff' : '#6b7280',
      transition: 'all 0.2s',
