import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, Settings } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [isStudent, setIsStudent] = useState(true);
  const [showPass, setShowPass] = useState(false);

  const [formData, setFormData] = useState({
    fullName: '',
    rollNo: '',
    department: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const styles = {
    // 1. PAGE CONTAINER (Split Screen Layout)
    pageContainer: {
      minHeight: '100vh',
      width: '100vw',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: "'Inter', sans-serif",
      // Background gradient for the whole page (Mobile view gets this)
      background: 'linear-gradient(135deg, #7c3aed 0%, #a855f7 100%)', // Purple Gradient
      padding: '20px' 
    },

    // 2. MAIN CARD WRAPPER (Desktop: Wide, Mobile: Narrow)
    mainWrapper: {
      display: 'flex',
      width: '100%',
      maxWidth: '900px', // Limits width on desktop
      height: '600px',
      backgroundColor: '#fff',
      borderRadius: '24px',
      overflow: 'hidden',
      boxShadow: '0 20px 60px rgba(0,0,0,0.2)',
    },

    // 3. LEFT PANEL (Hidden on Mobile)
    leftPanel: {
      flex: 1,
      background: 'linear-gradient(135deg, #6d28d9 0%, #9333ea 100%)', // Darker Purple
      display: 'none', // Default hidden (Mobile first)
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff',
      padding: '40px',
      textAlign: 'center'
    },
    // Show on Desktop (Media Query Logic handled via inline check below)

    logoIcon: { marginBottom: '20px' },
    welcomeTitle: { fontSize: '32px', fontWeight: '800', marginBottom: '10px' },
    welcomeText: { fontSize: '14px', opacity: 0.8, lineHeight: '1.6', maxWidth: '300px' },
    smallBtnGroup: { marginTop: '40px', display: 'flex', gap: '10px' },
    dot: (color) => ({ width: '12px', height: '12px', borderRadius: '50%', backgroundColor: color }),

    // 4. RIGHT PANEL (The Form)
    rightPanel: {
      flex: 1, // Takes full width on mobile
      padding: '40px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      backgroundColor: 'rgba(255,255,255,0.15)', // Glassy hint if needed
      backdropFilter: 'blur(10px)'
    },

    // Form Header
    header: { textAlign: 'center', marginBottom: '20px' },
    title: { fontSize: '24px', fontWeight: 'bold', color: '#1e293b', marginBottom: '5px' },

    // Inputs
    form: { display: 'flex', flexDirection: 'column', gap: '15px' },
    input: {
      width: '100%', padding: '12px 16px', borderRadius: '30px', // Rounded pills like image
      border: 'none', backgroundColor: '#f3f4f6', 
      fontSize: '14px', outline: 'none', color: '#333'
    },

    // Submit Button
    submitBtn: {
      width: '100%', padding: '14px', borderRadius: '30px', border: 'none',
      backgroundColor: '#fff', color: '#7c3aed', // White btn with purple text
      fontSize: '16px', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px',
      boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
    },

    // Checkbox Text
    terms: { fontSize: '12px', color: '#fff', display: 'flex', alignItems: 'center', gap: '6px', marginTop: '5px' },

    // Mobile Overrides (Since inline styles can't do media queries easily, we use window width check or simple flex tricks)
    mobileCardStyle: {
       background: 'rgba(255,255,255,0.2)', // Transparent white on purple bg
       backdropFilter: 'blur(10px)',
       border: '1px solid rgba(255,255,255,0.3)',
       color: '#fff' // Text becomes white on mobile purple bg
    }
  };

  // Simple Responsive Check (You can use CSS classes normally, this is for inline simplicity)
  const isDesktop = window.innerWidth > 768; 

  return (
    <div style={styles.pageContainer}>
      <div style={{
        ...styles.mainWrapper, 
        // On Mobile: Make wrapper transparent to blend with background
        backgroundColor: isDesktop ? '#fff' : 'transparent',
        boxShadow: isDesktop ? '0 20px 60px rgba(0,0,0,0.2)' : 'none',
        maxWidth: isDesktop ? '900px' : '400px',
        height: isDesktop ? '600px' : 'auto'
      }}>

        {/* LEFT PANEL (Desktop Only) */}
        <div style={{ 
          ...styles.leftPanel, 
          display: isDesktop ? 'flex' : 'none' 
        }}>
          <div style={styles.logoIcon}>
            <Settings size={60} color="#fff" />
          </div>
          <h1 style={styles.welcomeTitle}>EventSphere</h1>
          <p style={styles.welcomeText}>
            Join the ultimate campus event platform. Manage workshops, tickets, and attendance seamlessly.
          </p>
          <div style={styles.smallBtnGroup}>
             <div style={styles.dot('#fbbf24')}></div>
             <div style={styles.dot('#f87171')}></div>
             <div style={styles.dot('#34d399')}></div>
          </div>
        </div>

        {/* RIGHT PANEL (Form) */}
        <div style={{ 
          ...styles.rightPanel,
          // If on mobile, apply the "Glass Card" look
          ...(isDesktop ? {} : styles.mobileCardStyle),
          borderRadius: isDesktop ? '0' : '24px' // Round corners on mobile card
        }}>

          <div style={styles.header}>
            <h2 style={{...styles.title, color: isDesktop ? '#4c1d95' : '#fff'}}>Sign Up</h2>
            <p style={{fontSize:'13px', color: isDesktop ? '#6b7280' : 'rgba(255,255,255,0.8)'}}>Create your account</p>
          </div>

          <div style={styles.form}>
            <input style={styles.input} placeholder="Full Name" name="fullName" />
            <input style={styles.input} placeholder="Roll Number / ID" name="rollNo" />
            <input style={styles.input} placeholder="Email Address" name="email" type="email" />
            <input style={styles.input} placeholder="Password" name="password" type="password" />
            <input style={styles.input} placeholder="Confirm Password" name="confirmPassword" type="password" />

            <div style={styles.terms}>
               <input type="checkbox" style={{accentColor: '#fff'}}/>
               <span>I agree to the Terms & Privacy</span>
            </div>

            <button style={styles.submitBtn} onClick={() => { onLogin({}); navigate('/'); }}>Register</button>
          </div>

           <div style={{textAlign:'center', marginTop:'15px', fontSize:'13px', color: isDesktop ? '#6b7280' : '#fff'}}>
             Already have an account? <Link to="/signin" style={{color: isDesktop ? '#7c3aed' : '#fff', fontWeight:'bold'}}>Login</Link>
           </div>
        </div>

      </div>
    </div>
  );
};

export default StudentSignUp;
