import React from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, CheckCircle, Calendar, AlertCircle, Bell } from 'lucide-react';

const Notifications = ({ theme, notificationsList }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  // MOCK DATA
  const defaultNotifications = [
    { 
      id: 1, 
      type: 'success', 
      title: 'Registration Approved', 
      desc: "Your registration for 'AI & Future Tech Workshop' has been confirmed. Tap to view ticket.", 
      time: "2h ago", 
      eventId: 1, 
      icon: <CheckCircle size={24} color="#5c5cfc" />, 
      bgColor: '#eef2ff' 
    },
    { 
      id: 2, 
      type: 'new',
      title: 'New Event Added', 
      desc: "'Inter-College Music Battle' is now open for registration. Check details.", 
      time: "5h ago", 
      eventId: 2,
      icon: <Calendar size={24} color="#22c55e" />, 
      bgColor: '#dcfce7' 
    },
    { 
      id: 3, 
      type: 'update',
      title: 'Event Updated', 
      desc: "The location for 'University Basketball Finals' has been changed.", 
      time: "Yesterday", 
      eventId: 3,
      icon: <AlertCircle size={24} color="#eab308" />, 
      bgColor: '#fef9c3' 
    },
    { 
      id: 4, 
      type: 'reminder',
      title: 'Reminder', 
      desc: "Don't forget, 'AI & Future Tech Workshop' starts tomorrow at 09:00 AM.", 
      time: "2d ago", 
      eventId: 1,
      icon: <Bell size={24} color="#ef4444" />, 
      bgColor: '#fee2e2' 
    }
  ];

  const list = notificationsList && notificationsList.length > 0 ? notificationsList : defaultNotifications;

  const handleNotificationClick = (item) => {
    if (!item.eventId) return;
    if (item.type === 'success' || item.title.includes('Approved')) {
      navigate(`/ticket-confirmation/${item.eventId}`);
    } else {
      navigate(`/event-details/${item.eventId}`);
    }
  };

  const styles = {
    // 1. CONTAINER: Responsive Width & Padding
    container: {
      padding: '3vw', 
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      minHeight: '100vh',
      fontFamily: "'Inter', sans-serif",
      maxWidth: '60vw', // Restrict width on large screens for readability
      margin: '0 auto',
      width: '90vw' // Mobile fallback
    },
    header: {
      display: 'flex', 
      alignItems: 'center', 
      gap: '1.5vw', 
      marginBottom: '3vh'
    },
    backBtn: {
      cursor: 'pointer', 
      padding: '0.6vw', 
      borderRadius: '50%',
      backgroundColor: 'transparent', 
      border: 'none', 
      color: isDark ? '#fff' : '#1e293b'
    },
    title: {
      fontSize: '2vw', 
      fontWeight: '800', 
      color: isDark ? '#fff' : '#1e293b'
    },
    listContainer: {
      display: 'flex', 
      flexDirection: 'column', 
      gap: '2vh'
    },
    // 2. CARD: Responsive Padding & Radius
    card: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '1.5vw', 
      borderRadius: '1.5vw',
      display: 'flex', 
      gap: '1.5vw', 
      alignItems: 'flex-start',
      boxShadow: '0 0.5vh 1vh rgba(0,0,0,0.02)',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      cursor: 'pointer', 
      transition: 'transform 0.2s'
    },
    // 3. ICON BOX: Responsive Size
    iconBox: (bg) => ({
      width: '4vw', 
      height: '4vw', 
      borderRadius: '1vw', 
      backgroundColor: isDark ? '#334155' : bg,
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center', 
      flexShrink: 0
    }),
    content: { flex: 1 },
    
    // Header Row (Title + Time)
    rowTop: {
      display: 'flex', 
      justifyContent: 'space-between', 
      alignItems: 'flex-start', 
      marginBottom: '0.8vh'
    },
    mainTitle: {
      fontSize: '1.4vw', 
      fontWeight: '700', 
      color: isDark ? '#fff' : '#1e293b', 
      lineHeight: '1.2'
    },
    time: {
      fontSize: '1vw', 
      color: '#94a3b8', 
      fontWeight: '500', 
      whiteSpace: 'nowrap', 
      marginLeft: '1vw'
    },
    
    // Description Text
    desc: {
      fontSize: '1.1vw', 
      color: '#64748b', 
      lineHeight: '1.6'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <button style={styles.backBtn} onClick={() => navigate(-1)}>
          <ArrowLeft size={24} />
        </button>
        <h1 style={styles.title}>Notifications</h1>
      </div>

      <div style={styles.listContainer}>
        {list.map((item) => (
          <div 
            key={item.id} 
            style={styles.card}
            onClick={() => handleNotificationClick(item)}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
          >
            <div style={styles.iconBox(item.bgColor)}>
              {item.icon}
            </div>

            <div style={styles.content}>
              {/* TITLE & TIME ROW */}
              <div style={styles.rowTop}>
                <span style={styles.mainTitle}>{item.title}</span>
                <span style={styles.time}>{item.time}</span>
              </div>
              
              {/* DESCRIPTION */}
              <div style={styles.desc}>{item.desc}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Notifications;
