import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { ArrowLeft, Check } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    fullName: '',
    rollNo: '',
    department: '',
    email: '',
    password: '',
    confirmPassword: '',
    agree: false
  });

  const [showPass, setShowPass] = useState(false);
  const [errors, setErrors] = useState({});

  const styles = {
    container: {
      minHeight: '100vh',
      fontFamily: "'Segoe UI', sans-serif",
      backgroundColor: '#f1f5f9',
      padding: '20px',
      boxSizing: 'border-box'
    },
    formWrapper: {
      maxWidth: '450px',
      margin: '0 auto',
      backgroundColor: '#fff',
      borderRadius: '12px',
      padding: '30px',
      boxShadow: '0 4px 15px rgba(0,0,0,0.08)'
    },
    header: {
      textAlign: 'center',
      marginBottom: '25px'
    },
    backBtn: {
      display: 'inline-flex',
      alignItems: 'center',
      color: '#64748b',
      fontSize: '14px',
      cursor: 'pointer',
      marginBottom: '20px',
      padding: '8px 0'
    },
    title: {
      fontSize: '26px',
      fontWeight: '700',
      color: '#0f172a',
      margin: '0 0 5px 0'
    },
    subtitle: {
      fontSize: '14px',
      color: '#94a3b8'
    },

    toggleContainer: {
      display: 'flex',
      backgroundColor: '#f1f5f9',
      borderRadius: '10px',
      padding: '4px',
      marginBottom: '25px'
    },
    toggleBtn: (active) => ({
      flex: 1,
      border: 'none',
      padding: '12px',
      borderRadius: '8px',
      fontSize: '14px',
      fontWeight: '600',
      cursor: 'pointer',
      backgroundColor: active ? '#0ea5e9' : 'transparent',
      color: active ? '#fff' : '#64748b',
      transition: 'all 0.2s'
    }),

    form: {
      display: 'flex',
      flexDirection: 'column',
      gap: '18px'
    },
    row: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '15px'
    },
    col: {
      display: 'flex',
      flexDirection: 'column'
    },
    label: {
      fontSize: '13px',
      fontWeight: '600',
      color: '#334155',
      marginBottom: '6px'
    },
    input: (error) => ({
      width: '100%',
      padding: '12px 15px',
      borderRadius: '8px',
      border: `1px solid ${error ? '#ef4444' : '#e2e8f0'}`,
      fontSize: '14px',
      outline: 'none',
      backgroundColor: '#f8fafc',
      boxSizing: 'border-box',
      transition: 'all 0.2s',
      color: '#1e293b'
    }),
    error: {
      fontSize: '11px',
      color: '#ef4444',
      marginTop: '4px'
    },

    select: {
      width: '100%',
      padding: '12px 15px',
      borderRadius: '8px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      outline: 'none',
      backgroundColor: '#f8fafc',
      boxSizing: 'border-box',
      color: '#1e293b',
      cursor: 'pointer'
    },

    checkboxRow: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      fontSize: '13px',
      color: '#64748b'
    },
    checkbox: {
      width: '18px',
      height: '18px',
      accentColor: '#0ea5e9',
      cursor: 'pointer'
    },
    linkHighlight: {
      color: '#0ea5e9',
      fontWeight: '600',
      textDecoration: 'none'
    },

    submitBtn: {
      width: '100%',
      padding: '14px',
      borderRadius: '10px',
      border: 'none',
      backgroundColor: '#0ea5e9',
      color: '#fff',
      fontSize: '15px',
      fontWeight: '700',
      cursor: 'pointer',
      boxShadow: '0 4px 12px rgba(14, 165, 233, 0.3)',
      transition: 'all 0.2s',
      marginTop: '5px'
    },
    loginLink: {
      textAlign: 'center',
      fontSize: '13px',
      color: '#94a3b8',
      marginTop: '20px'
    },

    passRow: {
      display: 'grid',
      gridTemplateColumns: '1fr auto',
      gap: '10px',
      alignItems: 'start'
    },
    passWrapper: {
      position: 'relative'
    },
    passToggle: {
      position: 'absolute',
      right: '12px',
      top: '50%',
      transform: 'translateY(-50%)',
      background: 'none',
      border: 'none',
      cursor: 'pointer',
      color: '#94a3b8',
      padding: '4px',
      fontSize: '12px'
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.fullName.trim()) newErrors.fullName = 'Required';
    if (!formData.rollNo.trim()) newErrors.rollNo = 'Required';
    if (!formData.department || formData.department === 'Select') newErrors.department = 'Required';

    if (!formData.email.trim()) {
      newErrors.email = 'Required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Invalid email';
    }

    if (!formData.password) {
      newErrors.password = 'Required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Min 6 characters';
    }

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Mismatch';
    }

    if (!formData.agree) newErrors.agree = 'Required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      onLogin({ 
        name: formData.fullName || 'New Student', 
        email: formData.email, 
        role: 'student',
        college: 'Engineering Tech Institute' 
      });
      navigate('/');
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.formWrapper}>
        {/* Header */}
        <div style={styles.header}>
          <div style={styles.backBtn} onClick={() => navigate('/')}>
            <ArrowLeft size={16} style={{marginRight: 6}}/> Back
          </div>
          <h1 style={styles.title}>Create Account</h1>
          <p style={styles.subtitle}>Sign up to get started</p>
        </div>

        {/* Toggle */}
        <div style={styles.toggleContainer}>
          <button style={styles.toggleBtn(true)}>Student</button>
          <button style={styles.toggleBtn(false)} onClick={() => navigate('/teacher-signup')}>Teacher</button>
        </div>

        {/* Form */}
        <form style={styles.form} onSubmit={handleSubmit}>

          {/* Full Name */}
          <div style={styles.col}>
            <label style={styles.label}>Full Name</label>
            <input 
              style={styles.input(!!errors.fullName)} 
              placeholder="Enter your name" 
              name="fullName" 
              value={formData.fullName}
              onChange={handleChange}
            />
            {errors.fullName && <span style={styles.error}>{errors.fullName}</span>}
          </div>

          {/* Row: Roll No + Dept */}
          <div style={styles.row}>
            <div style={styles.col}>
              <label style={styles.label}>Roll Number</label>
              <input 
                style={styles.input(!!errors.rollNo)} 
                placeholder="ID-2024" 
                name="rollNo" 
                value={formData.rollNo}
                onChange={handleChange}
              />
              {errors.rollNo && <span style={styles.error}>{errors.rollNo}</span>}
            </div>
            <div style={styles.col}>
              <label style={styles.label}>Department</label>
              <select 
                style={styles.select} 
                name="department" 
                value={formData.department}
                onChange={handleChange}
              >
                <option value="Select">Select</option>
                <option value="CSE">CSE</option>
                <option value="ECE">ECE</option>
                <option value="MECH">MECH</option>
                <option value="CIVIL">CIVIL</option>
                <option value="IT">IT</option>
                <option value="EEE">EEE</option>
              </select>
              {errors.department && <span style={styles.error}>{errors.department}</span>}
            </div>
          </div>

          {/* Email */}
          <div style={styles.col}>
            <label style={styles.label}>Email</label>
            <input 
              style={styles.input(!!errors.email)} 
              type="email" 
              placeholder="student@college.edu" 
              name="email" 
              value={formData.email}
              onChange={handleChange}
            />
            {errors.email && <span style={styles.error}>{errors.email}</span>}
          </div>

          {/* Password Row */}
          <div style={styles.row}>
            <div style={styles.col}>
              <label style={styles.label}>Password</label>
              <div style={styles.passWrapper}>
                <input 
                  style={styles.input(!!errors.password)} 
                  type={showPass ? "text" : "password"} 
                  placeholder="Create password" 
                  name="password" 
                  value={formData.password}
                  onChange={handleChange}
                />
                <button 
                  type="button" 
                  style={styles.passToggle}
                  onClick={() => setShowPass(!showPass)}
                >
                  {showPass ? 'Hide' : 'Show'}
                </button>
              </div>
              {errors.password && <span style={styles.error}>{errors.password}</span>}
            </div>
            <div style={styles.col}>
              <label style={styles.label}>Confirm</label>
              <input 
                style={styles.input(!!errors.confirmPassword)} 
                type="password" 
                placeholder="Confirm" 
                name="confirmPassword" 
                value={formData.confirmPassword}
                onChange={handleChange}
              />
              {errors.confirmPassword && <span style={styles.error}>{errors.confirmPassword}</span>}
            </div>
          </div>

          {/* Terms */}
          <div style={styles.checkboxRow}>
            <input 
              type="checkbox" 
              style={styles.checkbox}
              checked={formData.agree}
              onChange={handleChange}
              name="agree"
              id="agree"
            />
            <label htmlFor="agree" style={{cursor: 'pointer', flex: 1}}>
              I agree to the <Link to="/terms" style={styles.linkHighlight}>Terms</Link> & <Link to="/privacy" style={styles.linkHighlight}>Privacy</Link>
            </label>
          </div>
          {errors.agree && <span style={styles.error}>{errors.agree}</span>}

          {/* Submit */}
          <button type="submit" style={styles.submitBtn}>
            Create Account
          </button>

          <div style={styles.loginLink}>
            Already have an account? <Link to="/signin" style={styles.linkHighlight}>Login</Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default StudentSignUp;
