import React from 'react';
import { Clock, MapPin, Search } from 'lucide-react';

const Events = ({ allEvents, theme, searchTerm, setSearchTerm, onRegister }) => {
  const isDark = theme === 'dark';

  // Filter events by search and categories
  const filteredEvents = allEvents.filter(event => 
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const styles = {
    card: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderRadius: '24px',
      overflow: 'hidden',
      marginBottom: '20px',
      border: isDark ? '1px solid #334155' : '1px solid #f1f5f9'
    },
    regBtn: (registered) => ({
      width: '100%',
      backgroundColor: registered ? '#16a34a' : '#5c5cfc',
      color: '#fff',
      border: 'none',
      padding: '12px',
      borderRadius: '12px',
      fontWeight: 'bold',
      cursor: registered ? 'default' : 'pointer'
    })
  };

  return (
    <div style={{ padding: '16px', backgroundColor: isDark ? '#0f172a' : '#fff' }}>
      {/* Category Tabs & Search Bar here... */}

      {filteredEvents.map(event => (
        <div key={event.id} style={styles.card}>
          <img src={event.image} alt={event.title} style={{ width: '100%', height: '180px', objectFit: 'cover' }} />
          <div style={{ padding: '16px' }}>
            <h4 style={{ margin: '0 0 8px 0', fontSize: '18px' }}>{event.title}</h4>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px', fontSize: '13px', color: '#94a3b8' }}>
              <Clock size={16} /> {event.date}
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px', fontSize: '13px', color: '#94a3b8', marginTop: '4px' }}>
              <MapPin size={16} /> {event.location}
            </div>

            {/* REGISTER BUTTON */}
            <div style={{ marginTop: '16px' }}>
              <button 
                style={styles.regBtn(event.isRegistered)}
                onClick={() => !event.isRegistered && onRegister(event.id)}
              >
                {event.isRegistered ? "✓ Registered" : "Register"}
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Events;
