import React, { useState } from 'react';
import { Bell, Clock, MapPin, X, CheckCircle2 } from 'lucide-react';

const Dashboard = ({ user, events, theme, regCount }) => {
  const isDark = theme === 'dark';
  const [isNotiOpen, setIsNotiOpen] = useState(false);

  const notifications = [
    { id: 1, title: "Registration Successful", desc: "You're in for Tech Summit 2024!", time: "2 mins ago" },
    { id: 2, title: "Event Reminder", desc: "Cultural Night starts in 1 hour.", time: "1 hour ago" }
  ];

  const styles = {
    // Main App Container
    container: { 
      maxWidth: '400px', 
      margin: '0 auto', 
      padding: '16px', 
      paddingBottom: '100px', 
      position: 'relative', 
      minHeight: '100vh',
      backgroundColor: isDark ? '#0f172a' : '#fff',
    },
    header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' },
    
    // --- MATCHED NOTIFICATION SLIDE ---
    notiOverlay: {
      position: 'fixed',
      top: 0, left: 0, right: 0, bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.4)',
      display: isNotiOpen ? 'block' : 'none',
      zIndex: 100,
      // Ensures overlay only covers the app area if desired, 
      // but usually, a full screen dim looks better.
    },
    notiPanel: {
      position: 'fixed',
      bottom: 0, 
      left: '50%', // Move to middle of screen
      // Center horizontally (-50%) and slide vertically based on state
      transform: isNotiOpen ? 'translateX(-50%) translateY(0)' : 'translateX(-50%) translateY(100%)',
      width: '100%',
      maxWidth: '400px', // EXACT MATCH to Dashboard width
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderTopLeftRadius: '28px',
      borderTopRightRadius: '28px',
      padding: '24px 20px',
      transition: 'transform 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
      zIndex: 101,
      maxHeight: '75vh',
      boxShadow: '0 -10px 25px rgba(0,0,0,0.1)'
    },

    // Card Styles
    statCard: { flex: 1, backgroundColor: isDark ? '#1e293b' : '#f8fafc', padding: '16px', borderRadius: '16px' },
    eventCard: { 
      backgroundColor: isDark ? '#1e293b' : '#fff', 
      borderRadius: '20px', padding: '12px', marginBottom: '16px',
      border: isDark ? '1px solid #334155' : '1px solid #f1f5f9' 
    }
  };

  return (
    <div style={styles.container}>
      
      {/* NOTIFICATION SLIDE (Now sized like Dashboard) */}
      <div style={styles.notiOverlay} onClick={() => setIsNotiOpen(false)} />
      <div style={styles.notiPanel}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h2 style={{ fontSize: '20px', fontWeight: 'bold' }}>Notifications</h2>
          <X size={24} onClick={() => setIsNotiOpen(false)} style={{ cursor: 'pointer' }} />
        </div>
        {notifications.map(item => (
          <div key={item.id} style={{ display: 'flex', gap: '12px', padding: '16px 0', borderBottom: isDark ? '1px solid #334155' : '1px solid #f1f5f9' }}>
            <CheckCircle2 size={18} color="#2563eb" />
            <div>
              <p style={{ margin: 0, fontWeight: 'bold', fontSize: '14px' }}>{item.title}</p>
              <p style={{ margin: '2px 0', fontSize: '12px', color: '#94a3b8' }}>{item.desc}</p>
            </div>
          </div>
        ))}
      </div>

      {/* DASHBOARD HEADER */}
      <header style={styles.header}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <div style={{ width: '36px', height: '36px', borderRadius: '50%', backgroundColor: '#2563eb', color: '#fff', display: 'flex', justifyContent: 'center', alignItems: 'center', fontWeight: 'bold' }}>ES</div>
          <span style={{ fontWeight: 'bold' }}>EventSphere</span>
        </div>
        <Bell size={22} onClick={() => setIsNotiOpen(true)} style={{ cursor: 'pointer' }} />
      </header>

      <h1 style={{ fontSize: '28px', fontWeight: 'bold' }}>Hi {user.name.split(' ')[0]} ðŸ‘‹</h1>
      <p style={{ color: '#64748b', fontSize: '14px', marginBottom: '24px' }}>Explore college events</p>

      {/* STATS */}
      <div style={{ display: 'flex', gap: '12px', marginBottom: '24px' }}>
        <div style={styles.statCard}>
          <span style={{ fontSize: '11px', color: '#64748b' }}>Upcoming</span>
          <div style={{ fontSize: '22px', fontWeight: 'bold' }}>{events.length}</div>
        </div>
        <div style={styles.statCard}>
          <span style={{ fontSize: '11px', color: '#64748b' }}>Registered</span>
          <div style={{ fontSize: '22px', fontWeight: 'bold' }}>{regCount}</div>
        </div>
      </div>

      {/* EVENT LIST */}
      <h3 style={{ fontSize: '16px', marginBottom: '16px' }}>Upcoming Events</h3>
      {events.map(event => (
        <div key={event.id} style={styles.eventCard}>
          <img src={event.image} alt={event.title} style={{ width: '100%', height: '160px', objectFit: 'cover', borderRadius: '14px', marginBottom: '12px' }} />
          <h4 style={{ margin: '0 0 6px 0', fontSize: '15px' }}>{event.title}</h4>
          <div style={{ display: 'flex', alignItems: 'center', gap: '6px', fontSize: '11px', color: '#94a3b8' }}>
            <Clock size={14} /> {event.date}
          </div>
          <button style={{ width: '100%', backgroundColor: '#2563eb', color: '#fff', border: 'none', padding: '12px', borderRadius: '12px', fontWeight: 'bold', marginTop: '12px' }}>
            Register
          </button>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
