import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import Profile from './pages/Profile';
import EditProfile from './pages/EditProfile';
import ChangePassword from './pages/ChangePassword';
import Settings from './pages/Settings';
import ThemeSelection from './pages/ThemeSelection';
import BottomNav from './components/BottomNav';

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [user, setUser] = useState({
    name: "Priya Sharma",
    email: "priya.sharma@university.edu",
    college: "Engineering Tech Institute",
    regNo: "ETI-2023-4589"
  });

  // Updated Event Data with Category and reliable Image URLs
  const [allEvents] = useState([
    { 
      id: 1, 
      title: "Campus Tech Summit 2024", 
      date: "Mar 18, 2024 · 10:00 AM", 
      location: "Main Auditorium, Block A", 
      category: "Tech",
      image: "https://images.unsplash.com/photo-1540575467063-178a50c2df87?w=800" 
    },
    { 
      id: 2, 
      title: "Annual Cultural Night", 
      date: "Mar 22, 2024 · 6:30 PM", 
      location: "Open Air Theatre", 
      category: "Cultural",
      image: "https://images.unsplash.com/photo-1514525253361-bee871870402?w=800" 
    },
    { 
      id: 3, 
      title: "Spring Sports Fest", 
      date: "Mar 30, 2024 · 8:00 AM", 
      location: "University Sports Ground", 
      category: "Sports",
      image: "https://images.unsplash.com/photo-1508098682722-e99c43a406b2?w=800" 
    }
  ]);

  const filteredEvents = allEvents.filter(event => 
    event.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Router>
      <div style={{ 
        maxWidth: '375px', margin: '0 auto', 
        backgroundColor: theme === 'dark' ? '#0f172a' : '#fff', 
        minHeight: '100vh', position: 'relative',
        overflow: 'hidden'
      }}>
        <Routes>
          <Route path="/" element={<Dashboard user={user} events={filteredEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />} />
          <Route path="/events" element={<Events allEvents={filteredEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />} />
          <Route path="/my-events" element={<MyEvents theme={theme} />} />
          <Route path="/profile" element={<Profile user={user} theme={theme} />} />
          <Route path="/edit-profile" element={<EditProfile user={user} setUser={setUser} theme={theme} />} />
          <Route path="/change-password" element={<ChangePassword theme={theme} />} />
          <Route path="/settings" element={<Settings theme={theme} />} />
          <Route path="/settings/theme" element={<ThemeSelection currentTheme={theme} setTheme={setTheme} />} />
        </Routes>
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
