import React, { useState } from 'react';
import { ArrowLeft, Eye, EyeOff } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const ChangePassword = ({ theme }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';
  const [show, setShow] = useState(false);

  const styles = {
    container: { maxWidth: '600px', margin: '0 auto', padding: '24px', fontFamily: 'sans-serif', color: isDark ? '#fff' : '#1e293b' },
    header: { display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '30px' },
    title: { fontSize: '24px', fontWeight: 'bold' },
    
    form: { display: 'flex', flexDirection: 'column', gap: '20px' },
    label: { display: 'block', fontSize: '14px', fontWeight: '600', marginBottom: '8px', color: '#64748b' },
    inputContainer: { position: 'relative' },
    input: { width: '100%', padding: '14px', borderRadius: '12px', border: '1px solid #e2e8f0', backgroundColor: isDark ? '#1e293b' : '#fff', color: isDark ? '#fff' : '#1e293b', outline: 'none', boxSizing: 'border-box' },
    
    btn: { width: '100%', padding: '16px', backgroundColor: '#3b82f6', color: '#fff', border: 'none', borderRadius: '12px', fontWeight: 'bold', fontSize: '16px', cursor: 'pointer', marginTop: '10px' }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <ArrowLeft style={{cursor:'pointer'}} onClick={() => navigate(-1)} />
        <h1 style={styles.title}>Change Password</h1>
      </div>

      <div style={styles.form}>
        <div><label style={styles.label}>Current Password</label><input style={styles.input} type="password" /></div>
        
        <div>
          <label style={styles.label}>New Password</label>
          <div style={styles.inputContainer}>
            <input style={styles.input} type={show ? "text" : "password"} />
            <div style={{position:'absolute', right:15, top:14, cursor:'pointer', color:'#94a3b8'}} onClick={() => setShow(!show)}>
              {show ? <EyeOff size={20}/> : <Eye size={20}/>}
            </div>
          </div>
        </div>

        <div><label style={styles.label}>Confirm New Password</label><input style={styles.input} type="password" /></div>

        <button style={styles.btn}>Update Password</button>
      </div>
    </div>
  );
};

export default ChangePassword;
