import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Search, Bell, Calendar, MapPin, Clock, ArrowRight } from 'lucide-react';

const Dashboard = ({ user, events, theme, regCount, unreadCount, onReadNotifications }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  const styles = {
    // 1. DASHBOARD CONTAINER
    container: {
      padding: '20px',
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      minHeight: '100vh',
      fontFamily: "'Inter', sans-serif",
      maxWidth: '1400px',
      margin: '0 auto'
    },

    // 2. HEADER SECTION
    header: {
      display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', flexWrap: 'wrap', gap: '20px'
    },
    // LEFT: Profile & Welcome
    profileSection: { display: 'flex', alignItems: 'center', gap: '15px', cursor: 'pointer' },
    avatar: { width: '50px', height: '50px', borderRadius: '50%', objectFit: 'cover', border: '2px solid #6366f1' },
    welcomeText: { display: 'flex', flexDirection: 'column' },
    hello: { fontSize: '14px', color: '#64748b', fontWeight: '500' },
    name: { fontSize: '18px', fontWeight: '800', color: isDark ? '#fff' : '#1e293b' },

    // RIGHT: Search & Notification
    rightHeader: { display: 'flex', alignItems: 'center', gap: '15px' },
    searchContainer: {
      display: 'flex', alignItems: 'center', backgroundColor: isDark ? '#1e293b' : '#ffffff',
      borderRadius: '50px', padding: '10px 20px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)',
      width: '400px', border: isDark ? '1px solid #334155' : '1px solid #e2e8f0'
    },
    searchIcon: { color: '#94a3b8', marginRight: '10px' },
    input: { border: 'none', outline: 'none', background: 'transparent', fontSize: '14px', color: isDark ? '#fff' : '#334155', flex: 1, fontWeight: '500' },

    iconBtn: {
      width: '45px', height: '45px', borderRadius: '50%', backgroundColor: isDark ? '#1e293b' : '#fff',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0', color: isDark ? '#fff' : '#6366f1',
      display: 'flex', alignItems: 'center', justifyContent: 'center', cursor: 'pointer',
      position: 'relative', boxShadow: '0 4px 15px rgba(0,0,0,0.05)', transition: 'transform 0.2s'
    },
    badge: { position: 'absolute', top: '0px', right: '0px', width: '12px', height: '12px', backgroundColor: '#ef4444', borderRadius: '50%', border: '2px solid #fff' },

    // 3. HERO BANNER
    heroBanner: {
      display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: isDark ? '#312e81' : '#e0e7ff',
      borderRadius: '20px', padding: '40px', marginBottom: '40px', position: 'relative', overflow: 'hidden', boxShadow: '0 10px 30px rgba(0,0,0,0.05)'
    },
    heroContent: { maxWidth: '600px', zIndex: 2 },
    heroTitle: { fontSize: '32px', fontWeight: '800', marginBottom: '16px', color: isDark ? '#fff' : '#1e1b4b', lineHeight: '1.2' },
    heroText: { fontSize: '16px', marginBottom: '24px', color: isDark ? '#c7d2fe' : '#4338ca', lineHeight: '1.6' },
    heroBtn: { backgroundColor: '#1e1b4b', color: '#fff', border: 'none', padding: '12px 28px', borderRadius: '10px', fontWeight: 'bold', fontSize: '15px', cursor: 'pointer', boxShadow: '0 4px 12px rgba(30, 27, 75, 0.3)' },
    heroImage: { height: '220px', objectFit: 'contain', display: window.innerWidth < 768 ? 'none' : 'block' },

    // 4. STATS & GRID
    statsRow: { display: 'flex', gap: '20px', marginBottom: '40px', flexWrap: 'wrap' },
    statCard: { flex: 1, minWidth: '220px', backgroundColor: isDark ? '#1e293b' : '#fff', padding: '24px', borderRadius: '16px', border: isDark ? '1px solid #334155' : '1px solid #e2e8f0', display: 'flex', alignItems: 'center', gap: '20px', boxShadow: '0 4px 6px rgba(0,0,0,0.02)' },
    statIconBox: (bg, color) => ({ width: '56px', height: '56px', borderRadius: '14px', backgroundColor: bg, color: color, display: 'flex', alignItems: 'center', justifyContent: 'center' }),
    sectionHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' },
    sectionTitle: { fontSize: '22px', fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b' },
    seeAll: { fontSize: '14px', color: '#4f46e5', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px', fontWeight: '600' },
    grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '24px' },
    eventCard: { backgroundColor: isDark ? '#1e293b' : '#fff', borderRadius: '16px', overflow: 'hidden', border: isDark ? '1px solid #334155' : '1px solid #e2e8f0', transition: 'all 0.2s', cursor: 'pointer', display: 'flex', flexDirection: 'column', boxShadow: '0 2px 10px rgba(0,0,0,0.03)' },
    cardImage: { width: '100%', height: '180px', objectFit: 'cover' },
    cardBody: { padding: '20px', flex: 1, display: 'flex', flexDirection: 'column' },
    cardTitle: { fontSize: '18px', fontWeight: 'bold', marginBottom: '10px', color: isDark ? '#fff' : '#1e293b' },
    cardMeta: { fontSize: '14px', color: '#64748b', marginBottom: '6px', display: 'flex', alignItems: 'center', gap: '8px' }
  };

  return (
    <div style={styles.container}>
      {/* HEADER */}
      <div style={styles.header}>
        <div style={styles.profileSection} onClick={() => navigate('/profile')}>
          <img src={user.profileImage} alt="Profile" style={styles.avatar} />
          <div style={styles.welcomeText}>
            <span style={styles.hello}>Welcome back,</span>
            <span style={styles.name}>{user.name.split(' ')[0]}</span>
          </div>
        </div>

        <div style={styles.rightHeader}>
          <div style={styles.searchContainer}>
            <Search size={20} style={styles.searchIcon} />
            <input style={styles.input} placeholder="Search events..." />
          </div>
          
          {/* THEME TOGGLE REMOVED FROM HERE */}

          <div 
            style={styles.iconBtn} 
            onClick={() => { onReadNotifications(); navigate('/notifications'); }}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
          >
            <Bell size={22} />
            {unreadCount > 0 && <div style={styles.badge}></div>}
          </div>
        </div>
      </div>

      {/* HERO & CONTENT */}
      <div style={styles.heroBanner}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>Discover New Skills!</h1>
          <p style={styles.heroText}>Join workshops, seminars, and events to boost your career.</p>
          <button style={styles.heroBtn} onClick={() => navigate('/events')}>Explore Events</button>
        </div>
        <img src="https://img.freepik.com/free-vector/happy-student-with-graduation-cap-diploma_23-2147954930.jpg?w=740" alt="Student" style={styles.heroImage} />
      </div>

      <div style={styles.statsRow}>
        <div style={styles.statCard}>
          <div style={styles.statIconBox('#e0e7ff', '#4338ca')}><Calendar size={24} /></div>
          <div><div style={{ fontSize: '24px', fontWeight: 'bold' }}>{events.length}</div><div style={{ color: '#64748b', fontSize: '14px' }}>Total Events</div></div>
        </div>
        <div style={styles.statCard}>
          <div style={styles.statIconBox('#dcfce7', '#15803d')}><Clock size={24} /></div>
          <div><div style={{ fontSize: '24px', fontWeight: 'bold' }}>{regCount}</div><div style={{ color: '#64748b', fontSize: '14px' }}>Registered</div></div>
        </div>
      </div>

      <div>
        <div style={styles.sectionHeader}>
          <div style={styles.sectionTitle}>Upcoming Events</div>
          <div style={styles.seeAll} onClick={() => navigate('/events')}>View all <ArrowRight size={16} /></div>
        </div>
        <div style={styles.grid}>
          {events.slice(0, 3).map(event => (
            <div key={event.id} style={styles.eventCard} onClick={() => navigate(`/event-details/${event.id}`)}>
              <img src={event.image} alt={event.title} style={styles.cardImage} />
              <div style={styles.cardBody}>
                <h3 style={styles.cardTitle}>{event.title}</h3>
                <div style={styles.cardMeta}><Calendar size={16} /> {event.date.split('·')[0]}</div>
                <div style={styles.cardMeta}><MapPin size={16} /> {event.location}</div>
                <div style={{ marginTop: 'auto', paddingTop: '15px' }}>
                  <span style={{ backgroundColor: '#f1f5f9', padding: '6px 12px', borderRadius: '20px', fontSize: '12px', fontWeight: '600', color: '#475569' }}>{event.category}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
