import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Camera } from 'lucide-react';

const EditProfile = ({ userData, setUserData }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState(userData);

  const handleSave = () => {
    setUserData(formData); // Update the global App state
    navigate('/profile'); // Go back to profile view
  };

  return (
    <div style={{ padding: '20px' }}>
      <header style={{ display: 'flex', alignItems: 'center', marginBottom: '30px' }}>
        <ArrowLeft size={24} onClick={() => navigate(-1)} style={{ cursor: 'pointer' }} />
        <h2 style={{ flex: 1, textAlign: 'center', fontSize: '18px', fontWeight: 'bold', margin: 0 }}>Edit Profile</h2>
        <div style={{ width: 24 }}></div>
      </header>

      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <div style={{ position: 'relative', width: '100px', height: '100px', margin: '0 auto' }}>
          <img src="https://via.placeholder.com/100" alt="Avatar" style={{ borderRadius: '50%', width: '100%' }} />
          <div style={{ position: 'absolute', bottom: 0, right: 0, backgroundColor: '#5c5cfc', padding: '6px', borderRadius: '50%', border: '3px solid #fff' }}>
            <Camera size={16} color="#fff" />
          </div>
        </div>
        <span style={{ color: '#5c5cfc', fontSize: '13px', fontWeight: 'bold', marginTop: '12px', display: 'block' }}>Change Profile Photo</span>
      </div>

      <div style={editStyles.inputGroup}>
        <label style={editStyles.label}>Full Name</label>
        <input 
          style={editStyles.input} 
          value={formData.name} 
          onChange={(e) => setFormData({...formData, name: e.target.value})} 
        />
      </div>

      <div style={editStyles.inputGroup}>
        <label style={editStyles.label}>College Name</label>
        <input 
          style={editStyles.input} 
          value={formData.college} 
          onChange={(e) => setFormData({...formData, college: e.target.value})} 
        />
      </div>

      <button onClick={handleSave} style={editStyles.saveBtn}>Save Changes</button>
    </div>
  );
};

const editStyles = {
  inputGroup: { marginBottom: '20px' },
  label: { fontSize: '12px', color: '#94a3b8', marginBottom: '8px', display: 'block' },
  input: { width: '100%', padding: '12px 16px', borderRadius: '12px', border: '1px solid #f1f5f9', backgroundColor: '#f8fafc', boxSizing: 'border-box' },
  saveBtn: { width: '100%', padding: '14px', backgroundColor: '#5c5cfc', color: '#fff', border: 'none', borderRadius: '12px', fontWeight: 'bold', marginTop: '20px', cursor: 'pointer' }
};

export default EditProfile;
