import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Calendar, MapPin, Users, ArrowLeft } from 'lucide-react';

const EventDetails = ({ allEvents, theme, onRegister }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isDark = theme === 'dark';
  
  // Find the event data based on the ID in the URL
  const event = allEvents.find(e => e.id === parseInt(id));

  if (!event) return <div style={{ padding: '20px', color: isDark ? '#fff' : '#000' }}>Event not found</div>;

  const styles = {
    // 1. MAIN CONTAINER: Locked to mobile width (Android style)
    container: {
      backgroundColor: isDark ? '#0f172a' : '#f8fafc',
      minHeight: '100vh',
      maxWidth: '430px', // LOCK WIDTH
      margin: '0 auto',   // CENTER IT
      position: 'relative',
      paddingBottom: '160px', // Space for fixed footer
      fontFamily: 'sans-serif',
      boxShadow: '0 0 20px rgba(0,0,0,0.1)'
    },
    topHeader: {
      padding: '16px',
      textAlign: 'center',
      backgroundColor: isDark ? '#1e293b' : '#fff',
      fontWeight: 'bold',
      fontSize: '18px',
      color: isDark ? '#fff' : '#1e293b',
      position: 'sticky',
      top: 0,
      zIndex: 10,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    bannerContainer: {
      width: '100%',
      height: '260px',
      position: 'relative'
    },
    bannerImage: {
      width: '100%',
      height: '100%',
      objectFit: 'cover'
    },
    contentCard: {
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderTopLeftRadius: '30px',
      borderTopRightRadius: '30px',
      marginTop: '-40px', // Creates the overlap effect
      padding: '24px',
      position: 'relative',
      zIndex: 1,
      minHeight: '500px'
    },
    badge: {
      display: 'inline-block',
      backgroundColor: '#5c5cfc',
      color: '#fff',
      padding: '6px 12px',
      borderRadius: '20px',
      fontSize: '12px',
      fontWeight: 'bold',
      marginBottom: '16px',
      textTransform: 'uppercase'
    },
    title: {
      fontSize: '24px',
      fontWeight: '800',
      color: isDark ? '#fff' : '#1e293b',
      marginBottom: '20px',
      lineHeight: '1.3'
    },
    infoRow: {
      display: 'flex',
      gap: '15px',
      marginBottom: '20px',
      alignItems: 'flex-start'
    },
    infoIcon: {
      color: '#64748b',
      marginTop: '2px',
      flexShrink: 0
    },
    infoText: {
      display: 'flex',
      flexDirection: 'column'
    },
    label: {
      fontWeight: 'bold',
      fontSize: '15px',
      color: isDark ? '#fff' : '#1e293b',
      marginBottom: '2px'
    },
    subLabel: {
      fontSize: '13px',
      color: '#94a3b8'
    },
    divider: {
      height: '1px',
      backgroundColor: isDark ? '#334155' : '#f1f5f9',
      margin: '24px 0'
    },
    aboutHeader: {
      fontSize: '18px',
      fontWeight: 'bold',
      color: isDark ? '#fff' : '#1e293b',
      marginBottom: '12px'
    },
    description: {
      color: '#94a3b8',
      fontSize: '14px',
      lineHeight: '1.6'
    },
    // 2. FOOTER: Fixed to bottom, locked to mobile width
    footer: {
      position: 'fixed',
      bottom: 0,
      left: '50%', // Move to center of screen
      transform: 'translateX(-50%)', // Align center
      width: '100%',
      maxWidth: '430px', // Match container width
      backgroundColor: isDark ? '#1e293b' : '#fff',
      padding: '20px 24px',
      borderTopLeftRadius: '24px',
      borderTopRightRadius: '24px',
      boxShadow: '0 -4px 20px rgba(0,0,0,0.08)',
      zIndex: 100,
      boxSizing: 'border-box'
    },
    registerBtn: {
      width: '100%',
      backgroundColor: '#5c5cfc',
      color: '#fff',
      padding: '16px',
      borderRadius: '12px',
      border: 'none',
      fontSize: '16px',
      fontWeight: 'bold',
      cursor: 'pointer',
      marginBottom: '12px'
    },
    backBtn: {
      width: '100%',
      backgroundColor: 'transparent',
      color: isDark ? '#fff' : '#1e293b',
      padding: '16px',
      borderRadius: '12px',
      border: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      fontSize: '16px',
      fontWeight: 'bold',
      cursor: 'pointer'
    }
  };

  return (
    <div style={styles.container}>
      {/* Top Header */}
      <div style={styles.topHeader}>
         Event Details
      </div>

      {/* Banner Image */}
      <div style={styles.bannerContainer}>
        <img src={event.image} alt={event.title} style={styles.bannerImage} />
        {/* Gradient Overlay */}
        <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', background: 'linear-gradient(to bottom, rgba(0,0,0,0) 60%, rgba(0,0,0,0.4) 100%)' }}></div>
      </div>

      {/* Overlapping Content Card */}
      <div style={styles.contentCard}>
        <span style={styles.badge}>{event.category || 'EVENT'}</span>
        <h1 style={styles.title}>{event.title}</h1>

        <div style={styles.infoRow}>
          <Calendar size={20} style={styles.infoIcon} />
          <div style={styles.infoText}>
            <span style={styles.label}>{event.date.split('·')[0]}</span>
            <span style={styles.subLabel}>{event.date.split('·')[1]}</span>
          </div>
        </div>

        <div style={styles.infoRow}>
          <MapPin size={20} style={styles.infoIcon} />
          <div style={styles.infoText}>
            <span style={styles.label}>{event.location}</span>
            <span style={styles.subLabel}>Central Campus</span>
          </div>
        </div>

        <div style={styles.infoRow}>
          <Users size={20} style={styles.infoIcon} />
          <div style={styles.infoText}>
            <span style={styles.label}>45 Seats Left</span>
            <span style={styles.subLabel}>Hurry up, filling fast!</span>
          </div>
        </div>

        <div style={styles.divider}></div>

        <h3 style={styles.aboutHeader}>About Event</h3>
        <p style={styles.description}>
          Join us for the biggest student tech summit of the year. Explore the latest in AI, Blockchain, and Cloud Computing with industry experts. Lunch and certificates provided for all attendees.
        </p>
      </div>

      {/* Footer Buttons */}
      <div style={styles.footer}>
        {!event.isRegistered ? (
          <button 
            style={styles.registerBtn}
            onClick={() => {
              onRegister(event.id);
              // NAVIGATE TO THE TICKET SLIDE
              navigate(`/ticket-confirmation/${event.id}`);
            }}
          >
            Register Now
          </button>
        ) : (
          <button 
            style={{ ...styles.registerBtn, backgroundColor: '#22c55e', cursor: 'default' }}
            disabled
          >
            ✓ Registration Confirmed
          </button>
        )}

        <button 
          style={styles.backBtn} 
          onClick={() => navigate(-1)} 
        >
          Back
        </button>
      </div>
    </div>
  );
};

export default EventDetails;
