import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Page Imports
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import MyEvents from './pages/MyEvents';
import EventDetails from './pages/EventDetails'; 
import BottomNav from './components/BottomNav';

function App() {
  const [theme, setTheme] = useState('light'); 
  const [searchTerm, setSearchTerm] = useState("");
  const [allEvents, setAllEvents] = useState([
    { 
      id: 1, 
      title: "AI & Future Tech Workshop", 
      date: "Apr 05, 2024 路 09:00 AM", 
      location: "Innovation Lab, 3rd Floor", 
      category: "Tech",
      image: "https://images.unsplash.com/photo-1485827404703-89b55fcc595e?w=800&q=80",
      isRegistered: false 
    },
    { 
      id: 2, 
      title: "Inter-College Music Battle", 
      date: "Apr 12, 2024 路 05:00 PM", 
      location: "Main Stadium", 
      category: "Cultural",
      image: "https://images.unsplash.com/photo-1501281668745-f7f57925c3b4?w=800&q=80",
      isRegistered: false
    }
  ]);

  // The function that actually registers the user
  const handleRegister = (eventId) => {
    setAllEvents(prev => prev.map(ev => 
      ev.id === eventId ? { ...ev, isRegistered: true } : ev
    ));
  };

  const registeredEvents = allEvents.filter(e => e.isRegistered);

  return (
    <Router>
      <div style={{ maxWidth: '430px', margin: '0 auto', minHeight: '100vh', position: 'relative', backgroundColor: theme === 'dark' ? '#0f172a' : '#fff' }}>
        <Routes>
          <Route path="/" element={<Dashboard events={allEvents} theme={theme} regCount={registeredEvents.length} />} />
          <Route path="/events" element={<Events allEvents={allEvents} theme={theme} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />} />
          <Route path="/my-events" element={<MyEvents theme={theme} events={registeredEvents} />} />
          
          {/* THE CONNECTION: Full screen details route */}
          <Route 
            path="/event-details/:id" 
            element={<EventDetails allEvents={allEvents} theme={theme} onRegister={handleRegister} />} 
          />
        </Routes>
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
