import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, Settings } from 'lucide-react';

const StudentSignUp = ({ onLogin }) => {
  const navigate = useNavigate();
  const [isStudent, setIsStudent] = useState(true);

  const [formData, setFormData] = useState({
    fullName: '',
    collegeName: '', // NEW FIELD
    rollNo: '',
    department: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const styles = {
    // 1. PAGE CONTAINER (Fixed, No Scroll)
    pageContainer: {
      height: '100vh',
      width: '100vw',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: "'Inter', sans-serif",
      background: 'linear-gradient(135deg, #7c3aed 0%, #a855f7 100%)', // Purple Gradient
      overflow: 'hidden' 
    },

    // 2. MAIN CARD (Split Screen)
    mainWrapper: {
      display: 'flex',
      width: '90%',
      maxWidth: '900px',
      height: '85vh', 
      maxHeight: '600px',
      backgroundColor: '#fff',
      borderRadius: '24px',
      overflow: 'hidden',
      boxShadow: '0 20px 60px rgba(0,0,0,0.2)',
    },

    // 3. LEFT PANEL (Hidden on Mobile)
    leftPanel: {
      flex: 1,
      background: 'linear-gradient(135deg, #6d28d9 0%, #9333ea 100%)',
      display: 'none', 
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff',
      padding: '40px',
      textAlign: 'center'
    },

    logoIcon: { marginBottom: '20px' },
    welcomeTitle: { fontSize: '28px', fontWeight: '800', marginBottom: '10px' },
    welcomeText: { fontSize: '14px', opacity: 0.8, lineHeight: '1.6', maxWidth: '280px' },

    // 4. RIGHT PANEL (The Form)
    rightPanel: {
      flex: 1.2, 
      padding: '30px', 
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      backgroundColor: '#fff',
      overflowY: 'auto' 
    },

    // Header
    header: { textAlign: 'center', marginBottom: '15px' },
    title: { fontSize: '22px', fontWeight: '800', color: '#1e293b', marginBottom: '4px' },
    subtitle: { fontSize: '13px', color: '#64748b' },

    // TOGGLE SWITCH
    toggleContainer: {
      display: 'flex',
      backgroundColor: '#f1f5f9',
      borderRadius: '10px',
      padding: '4px',
      marginBottom: '15px'
    },
    toggleBtn: (active) => ({
      flex: 1, padding: '8px', borderRadius: '8px', border: 'none',
      fontSize: '13px', fontWeight: '600', cursor: 'pointer',
      backgroundColor: active ? '#7c3aed' : 'transparent', // Purple active
      color: active ? '#fff' : '#64748b',
      transition: 'all 0.2s'
    }),

    // Form Grid
    form: { display: 'flex', flexDirection: 'column', gap: '10px' },
    input: {
      width: '100%', padding: '12px', borderRadius: '10px',
      border: '1px solid #e2e8f0', backgroundColor: '#f8fafc', 
      fontSize: '13px', outline: 'none', color: '#333',
      boxSizing: 'border-box'
    },
    row: { display: 'flex', gap: '10px' },
    col: { flex: 1 },

    // Submit
    submitBtn: {
      width: '100%', padding: '14px', borderRadius: '12px', border: 'none',
      backgroundColor: '#7c3aed', color: '#fff',
      fontSize: '15px', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px',
      boxShadow: '0 4px 15px rgba(124, 58, 237, 0.3)'
    },

    terms: { fontSize: '11px', color: '#64748b', display: 'flex', alignItems: 'center', gap: '6px', marginTop: '5px' },

    // Mobile Overrides
    mobileCardStyle: {
       background: 'rgba(255,255,255,0.95)', 
       borderRadius: '20px'
    }
  };

  const isDesktop = window.innerWidth > 768; 

  const handleChange = (e) => setFormData({...formData, [e.target.name]: e.target.value});

  const handleSubmit = () => {
    onLogin({ 
      name: formData.fullName || 'New User',
      college: formData.collegeName || 'Engineering Tech Institute', // Pass College Name
      role: isStudent ? 'student' : 'teacher',
    });
    navigate('/');
  };

  return (
    <div style={styles.pageContainer}>
      <div style={{
        ...styles.mainWrapper, 
        backgroundColor: isDesktop ? '#fff' : 'transparent',
        boxShadow: isDesktop ? '0 20px 60px rgba(0,0,0,0.2)' : 'none', 
        height: isDesktop ? '600px' : 'auto', 
        maxWidth: isDesktop ? '900px' : '400px'
      }}>

        {/* LEFT PANEL */}
        <div style={{ ...styles.leftPanel, display: isDesktop ? 'flex' : 'none' }}>
          <div style={styles.logoIcon}><Settings size={50} color="#fff" /></div>
          <h1 style={styles.welcomeTitle}>EventSphere</h1>
          <p style={styles.welcomeText}>Manage workshops, tickets, and attendance seamlessly.</p>
        </div>

        {/* RIGHT PANEL */}
        <div style={{ ...styles.rightPanel, ...(isDesktop ? {} : styles.mobileCardStyle) }}>

          <div style={styles.header}>
            <h2 style={styles.title}>Create Account</h2>
            <p style={styles.subtitle}>Sign up to get started</p>
          </div>

          <div style={styles.toggleContainer}>
            <button style={styles.toggleBtn(isStudent)} onClick={() => setIsStudent(true)}>Student</button>
            <button style={styles.toggleBtn(!isStudent)} onClick={() => setIsStudent(false)}>Teacher</button>
          </div>

          <div style={styles.form}>
            {/* Full Name */}
            <input style={styles.input} placeholder="Full Name" name="fullName" onChange={handleChange} />

            {/* NEW: College Name */}
            <input style={styles.input} placeholder="College Name" name="collegeName" onChange={handleChange} />

            {/* Split Row: Roll No & Dept */}
            <div style={styles.row}>
               <div style={styles.col}>
                 <input style={styles.input} placeholder={isStudent ? "Roll No" : "Emp ID"} name="rollNo" onChange={handleChange} />
               </div>
               <div style={styles.col}>
                 <select style={styles.input} name="department" onChange={handleChange}>
                   <option>Select Dept</option>
                   <option>CSE</option>
                   <option>ECE</option>
                   <option>MECH</option>
                 </select>
               </div>
            </div>

            <input style={styles.input} placeholder="Email Address" name="email" type="email" onChange={handleChange} />

            {/* Password Row */}
            <div style={styles.row}>
              <div style={styles.col}>
                 <input style={styles.input} placeholder="Password" name="password" type="password" onChange={handleChange} />
              </div>
              <div style={styles.col}>
                 <input style={styles.input} placeholder="Confirm" name="confirmPassword" type="password" onChange={handleChange} />
              </div>
            </div>

            <div style={styles.terms}>
               <input type="checkbox" />
               <span>I agree to Terms & Privacy</span>
            </div>

            <button style={styles.submitBtn} onClick={handleSubmit}>Register</button>
          </div>

           <div style={{textAlign:'center', marginTop:'12px', fontSize:'12px', color:'#64748b'}}>
             Already have an account? <Link to="/signin" style={{color: '#7c3aed', fontWeight:'bold', textDecoration:'none'}}>Login</Link>
           </div>
        </div>

      </div>
    </div>
  );
};

export default StudentSignUp;
