import React from 'react';
import { Bell, CheckCircle, Calendar, RefreshCw, ArrowLeft } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const Notifications = ({ theme }) => {
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  const notifications = [
    {
      id: 1,
      title: "Registration approved",
      desc: "Your account has been verified. You can now access all features.",
      time: "2h ago",
      icon: <CheckCircle size={20} color="#5c5cfc" />,
      bgColor: isDark ? 'rgba(92, 92, 252, 0.1)' : '#f0f0ff'
    },
    {
      id: 2,
      title: "New event added",
      desc: "Summer Workshop has been added to your calendar.",
      time: "Yesterday",
      icon: <Calendar size={20} color="#22c55e" />,
      bgColor: isDark ? 'rgba(34, 197, 94, 0.1)' : '#f0fff4'
    },
    {
      id: 3,
      title: "Event updated",
      desc: "The location for Team Lunch has been changed to Main Hall.",
      time: "Yesterday",
      icon: <RefreshCw size={20} color="#f59e0b" />,
      bgColor: isDark ? 'rgba(245, 158, 11, 0.1)' : '#fffdf0'
    }
  ];

  const styles = {
    container: {
      padding: '20px',
      backgroundColor: isDark ? '#0f172a' : '#fff',
      minHeight: '100vh',
      paddingBottom: '100px' // Space for the BottomNav icons
    },
    header: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      marginBottom: '30px'
    },
    card: {
      display: 'flex',
      gap: '15px',
      padding: '16px',
      borderRadius: '16px',
      border: isDark ? '1px solid #1e293b' : '1px solid #f1f5f9',
      marginBottom: '15px',
      backgroundColor: isDark ? '#1e293b' : '#fff'
    },
    iconBox: {
      width: '45px',
      height: '45px',
      borderRadius: '12px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      flexShrink: 0
    }
  };

  return (
    <div style={styles.container}>
      {/* Header with Back Button */}
      <div style={styles.header}>
        <ArrowLeft 
          size={24} 
          onClick={() => navigate(-1)} 
          style={{ cursor: 'pointer', color: isDark ? '#fff' : '#1e293b' }} 
        />
        <h2 style={{ margin: 0, fontSize: '24px', fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b' }}>
          Notifications
        </h2>
      </div>

      {/* Notification List */}
      {notifications.map((note) => (
        <div key={note.id} style={styles.card}>
          <div style={{ ...styles.iconBox, backgroundColor: note.bgColor }}>
            {note.icon}
          </div>
          <div style={{ flex: 1 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '4px' }}>
              <span style={{ fontWeight: 'bold', color: isDark ? '#fff' : '#1e293b' }}>{note.title}</span>
              <span style={{ fontSize: '12px', color: '#94a3b8' }}>{note.time}</span>
            </div>
            <p style={{ margin: 0, fontSize: '14px', color: '#64748b', lineHeight: '1.4' }}>
              {note.desc}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Notifications;
