import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import MyEvents from './pages/MyEvents';
import BottomNav from './components/BottomNav';

function App() {
  const [theme, setTheme] = useState('light');
  
  // 1. Central Event State
  const [events, setEvents] = useState([
    {
      id: 1,
      title: "AI & Future Tech Workshop",
      date: "Apr 05, 2024 - 09:00 AM",
      location: "Tech Hub, Room 101",
      image: "https://images.unsplash.com/photo-1485827404703-89b55fcc595e",
      isRegistered: false // Tracks if user joined
    },
    {
      id: 2,
      title: "Inter-College Music Battle",
      date: "Apr 12, 2024 - 05:00 PM",
      location: "Main Auditorium",
      image: "https://images.unsplash.com/photo-1501281668745-f7f57925c3b4",
      isRegistered: false
    },
    {
      id: 3,
      title: "Campus Cultural Night",
      date: "Apr 20, 2024 - 07:00 PM",
      location: "Open Air Theatre",
      image: "https://images.unsplash.com/photo-1533174072545-7a4b6ad7a6c3",
      isRegistered: false
    }
  ]);

  // 2. Registration Handler
  const handleRegister = (eventId) => {
    setEvents(prevEvents => 
      prevEvents.map(event => 
        event.id === eventId ? { ...event, isRegistered: true } : event
      )
    );
  };

  // 3. Derived State for Counters
  const regCount = events.filter(e => e.isRegistered).length;
  const user = { name: "Priya" };

  return (
    <Router>
      <div style={{ backgroundColor: theme === 'dark' ? '#0f172a' : '#fff', minHeight: '100vh' }}>
        <Routes>
          <Route 
            path="/" 
            element={
              <Dashboard 
                user={user} 
                events={events} 
                theme={theme} 
                regCount={regCount} 
                onRegister={handleRegister} 
              />
            } 
          />
          <Route 
            path="/my-events" 
            element={
              <MyEvents 
                events={events.filter(e => e.isRegistered)} 
                theme={theme} 
              />
            } 
          />
        </Routes>
        
        {/* Bottom Navigation persists across pages */}
        <BottomNav theme={theme} />
      </div>
    </Router>
  );
}

export default App;
