import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Clock, MapPin, Users, CheckCircle } from 'lucide-react';

const EventDetails = ({ allEvents, theme, onRegister }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isDark = theme === 'dark';

  // Find the event in your central list
  const event = allEvents.find(e => e.id === parseInt(id));

  if (!event) return <div style={{ padding: '20px' }}>Event Not Found</div>;

  const styles = {
    container: { height: '100vh', overflowY: 'auto', backgroundColor: isDark ? '#0f172a' : '#fff' },
    image: { width: '100%', height: '250px', objectFit: 'cover' },
    backBtn: { position: 'absolute', top: '20px', left: '20px', backgroundColor: '#fff', borderRadius: '50%', padding: '8px', cursor: 'pointer' },
    content: { padding: '20px' },
    footer: { position: 'fixed', bottom: 0, width: '100%', maxWidth: '375px', padding: '20px', backgroundColor: isDark ? '#1e293b' : '#fff' }
  };

  return (
    <div style={styles.container}>
      <div style={styles.backBtn} onClick={() => navigate(-1)}>
        <ArrowLeft color="#000" size={24} />
      </div>
      
      <img src={event.image} alt={event.title} style={styles.image} />
      
      <div style={styles.content}>
        <h1 style={{ fontSize: '24px', fontWeight: 'bold' }}>{event.title}</h1>
        <p style={{ color: '#5c5cfc', fontWeight: 'bold' }}>{event.category}</p>
        
        <div style={{ marginTop: '20px', display: 'flex', flexDirection: 'column', gap: '15px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}><Clock size={18} /> {event.date}</div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}><MapPin size={18} /> {event.location}</div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}><Users size={18} /> 45 Seats Left</div>
        </div>

        <h4 style={{ marginTop: '30px' }}>About Event</h4>
        <p style={{ color: '#94a3b8', lineHeight: '1.6' }}>
          Join us for a deep dive into {event.title}. This session covers industry standards and hands-on practice.
        </p>
      </div>

      <div style={styles.footer}>
        {/* ONLY REGISTER UNREGISTERED EVENTS */}
        {!event.isRegistered ? (
          <button 
            style={{ width: '100%', backgroundColor: '#5c5cfc', color: '#fff', padding: '16px', borderRadius: '16px', border: 'none', fontWeight: 'bold' }}
            onClick={() => { onRegister(event.id); navigate('/my-events'); }}
          >
            Register Now
          </button>
        ) : (
          <div style={{ textAlign: 'center', color: '#16a34a', fontWeight: 'bold', display: 'flex', justifyContent: 'center', gap: '8px' }}>
            <CheckCircle /> Already Registered
          </div>
        )}
      </div>
    </div>
  );
};

export default EventDetails;
