import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Home, Calendar, Ticket, User } from 'lucide-react';

const BottomNav = ({ theme }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const isDark = theme === 'dark';

  // ✅ FIXED: Hardcoded labels instead of using 't'
  const navItems = [
    { id: 'home', label: 'Home', icon: <Home size={24} />, path: '/' },
    { id: 'events', label: 'Events', icon: <Calendar size={24} />, path: '/events' },
    { id: 'tickets', label: 'Tickets', icon: <Ticket size={24} />, path: '/my-events' },
    { id: 'profile', label: 'Profile', icon: <User size={24} />, path: '/profile' },
  ];

  const styles = {
    container: {
      position: 'fixed',
      bottom: 0,
      left: 0,
      width: '100%',
      height: '80px', // Fixed height for mobile nav
      backgroundColor: isDark ? '#1e293b' : '#fff',
      borderTop: isDark ? '1px solid #334155' : '1px solid #e2e8f0',
      display: 'flex',
      justifyContent: 'space-around',
      alignItems: 'center',
      zIndex: 1000,
      boxShadow: '0 -4px 20px rgba(0,0,0,0.05)',
      paddingBottom: '10px' // Safe area for newer phones
    },
    navItem: (isActive) => ({
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '4px',
      color: isActive ? '#6366f1' : (isDark ? '#94a3b8' : '#64748b'),
      cursor: 'pointer',
      width: '60px',
      transition: 'all 0.2s ease'
    }),
    label: (isActive) => ({
      fontSize: '11px',
      fontWeight: isActive ? '700' : '500',
    })
  };

  return (
    <div style={styles.container}>
      {navItems.map((item) => {
        const isActive = location.pathname === item.path;
        
        return (
          <div 
            key={item.id} 
            style={styles.navItem(isActive)} 
            onClick={() => navigate(item.path)}
          >
            {item.icon}
            <span style={styles.label(isActive)}>{item.label}</span>
          </div>
        );
      })}
    </div>
  );
};

export default BottomNav;
