import React from 'react';
import { ArrowLeft, CheckCircle2, Calendar, RefreshCw, MessageSquare } from 'lucide-react';

const Notifications = ({ theme, setIsOpen }) => {
  const isDark = theme === 'dark';

  const notifications = [
    { 
      id: 1, 
      title: "Registration approved", 
      desc: "Your account has been verified. You can now access all features.", 
      time: "2h ago", 
      icon: <CheckCircle2 size={18} />,
      bgColor: '#eef2ff',
      iconColor: '#4f46e5'
    },
    { 
      id: 2, 
      title: "New event added", 
      desc: "Summer Workshop has been added to your calendar.", 
      time: "Yesterday", 
      icon: <Calendar size={18} />,
      bgColor: '#f0fdf4',
      iconColor: '#16a34a'
    },
    { 
      id: 3, 
      title: "Event updated", 
      desc: "The location for Team Lunch has been changed to Main Hall.", 
      time: "Yesterday", 
      icon: <RefreshCw size={18} />,
      bgColor: '#fff7ed',
      iconColor: '#ea580c'
    }
  ];

  return (
    <div style={{ width: '100%' }}>
      {/* Back Header */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '30px' }}>
        <ArrowLeft 
          size={24} 
          onClick={() => setIsOpen(false)} // This "switches" back to Dashboard
          style={{ cursor: 'pointer', color: isDark ? '#fff' : '#1e293b' }} 
        />
        <h2 style={{ fontSize: '22px', fontWeight: 'bold', margin: 0, color: isDark ? '#fff' : '#1e293b' }}>
          Notifications
        </h2>
      </div>

      {/* Notification Cards */}
      {notifications.map(n => (
        <div key={n.id} style={{
          display: 'flex',
          gap: '12px',
          padding: '16px',
          backgroundColor: isDark ? '#1e293b' : '#fff',
          borderRadius: '16px',
          marginBottom: '12px',
          border: isDark ? '1px solid #334155' : '1px solid #f1f5f9'
        }}>
          <div style={{ 
            width: '40px', height: '40px', borderRadius: '12px', 
            backgroundColor: n.bgColor, color: n.iconColor,
            display: 'flex', justifyContent: 'center', alignItems: 'center', flexShrink: 0
          }}>
            {n.icon}
          </div>
          <div style={{ flex: 1 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <p style={{ margin: 0, fontWeight: 'bold', fontSize: '14px', color: n.iconColor }}>{n.title}</p>
              <span style={{ fontSize: '11px', color: '#94a3b8' }}>{n.time}</span>
            </div>
            <p style={{ margin: '4px 0 0 0', fontSize: '13px', color: isDark ? '#94a3b8' : '#64748b' }}>{n.desc}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Notifications;
