import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Calendar, MapPin, Share2, ArrowLeft } from 'lucide-react';

const EventDetails = ({ allEvents, theme, onRegister }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const event = allEvents.find(e => e.id === parseInt(id));
  const isDark = theme === 'dark';

  if (!event) return <div>Event not found</div>;

  const styles = {
    // Main Container
    container: {
      maxWidth: '1000px',
      margin: '0 auto',
      padding: '24px',
      fontFamily: 'sans-serif',
      color: isDark ? '#fff' : '#1e293b',
      paddingBottom: '40px', // Standard padding
    },
    
    // Header Navigation
    headerNav: {
      display: 'flex', 
      justifyContent: 'space-between', 
      marginBottom: '20px'
    },
    iconBtn: {
      background: 'none', 
      border: 'none', 
      cursor: 'pointer',
      color: isDark ? '#fff' : '#1e293b',
      padding: '8px',
      borderRadius: '50%',
      backgroundColor: isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)'
    },
    
    // Main Content Grid
    grid: {
      display: 'grid',
      // Desktop: Image Left, Info Right. Mobile: Stacked.
      gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
      gap: '40px',
      alignItems: 'start'
    },
    
    // Left: Image
    image: {
      width: '100%',
      height: '400px', // Taller image for better look
      objectFit: 'cover',
      borderRadius: '24px',
      boxShadow: '0 10px 30px rgba(0,0,0,0.15)'
    },

    // Right: Info Column
    infoCol: { 
      display: 'flex', 
      flexDirection: 'column', 
      gap: '24px' 
    },
    title: { 
      fontSize: '32px', 
      fontWeight: '800', 
      lineHeight: '1.2',
      marginBottom: '5px'
    },
    
    // Icon Rows
    row: { 
      display: 'flex', 
      alignItems: 'center', 
      gap: '16px' 
    },
    iconBox: {
      width: '56px', 
      height: '56px', 
      borderRadius: '16px',
      backgroundColor: isDark ? '#1e293b' : '#eff6ff',
      color: '#3b82f6',
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      border: isDark ? '1px solid #334155' : 'none'
    },
    label: { 
      fontSize: '13px', 
      color: '#64748b', 
      fontWeight: '600', 
      display: 'block',
      marginBottom: '4px'
    },
    value: { 
      fontSize: '16px', 
      fontWeight: '700' 
    },

    // Description Section
    descTitle: { 
      fontSize: '20px', 
      fontWeight: 'bold', 
      marginBottom: '12px' 
    },
    descText: { 
      lineHeight: '1.6', 
      color: isDark ? '#cbd5e1' : '#475569',
      fontSize: '15px' 
    },

    // The Register Button (Now static, not floating)
    registerBtn: {
      width: '100%',
      padding: '18px',
      backgroundColor: '#3b82f6',
      color: '#fff',
      border: 'none',
      borderRadius: '16px',
      fontSize: '16px',
      fontWeight: 'bold',
      cursor: 'pointer',
      boxShadow: '0 8px 25px rgba(59, 130, 246, 0.4)',
      marginTop: '20px', // Spacing from text
      transition: 'transform 0.2s',
      textAlign: 'center'
    }
  };

  return (
    <div style={styles.container}>
      {/* Navigation */}
      <div style={styles.headerNav}>
        <button style={styles.iconBtn} onClick={() => navigate(-1)}>
          <ArrowLeft size={24} />
        </button>
        <button style={styles.iconBtn}>
          <Share2 size={24} />
        </button>
      </div>

      <div style={styles.grid}>
        {/* Left: Image */}
        <img src={event.image} alt={event.title} style={styles.image} />

        {/* Right: Details */}
        <div style={styles.infoCol}>
          <div>
            <h1 style={styles.title}>{event.title}</h1>
          </div>
          
          <div style={styles.row}>
            <div style={styles.iconBox}><Calendar size={28} /></div>
            <div>
              <span style={styles.label}>Date & Time</span>
              <span style={styles.value}>{event.date}</span>
            </div>
          </div>

          <div style={styles.row}>
            <div style={styles.iconBox}><MapPin size={28} /></div>
            <div>
              <span style={styles.label}>Location</span>
              <span style={styles.value}>{event.location}</span>
            </div>
          </div>

          <div>
            <h3 style={styles.descTitle}>About Event</h3>
            <p style={styles.descText}>
              Join us for an immersive experience learning about the latest in technology. 
              This workshop is suitable for all levels. Certificate provided upon completion.
            </p>
          </div>

          {/* BUTTON PLACED HERE - Below "About Event" */}
          <button 
            style={styles.registerBtn} 
            onClick={() => { onRegister(event.id); navigate('/my-events'); }}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
          >
            Register Now
          </button>
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
