import React from 'react';
import { ArrowLeft } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const GradientHeader = ({ title, subtitle, showBack = false, rightElement }) => {
  const navigate = useNavigate();

  const styles = {
    header: {
      background: 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%)', // Blue to Purple
      padding: '24px 24px 32px 24px',
      color: '#fff',
      borderBottomLeftRadius: '24px',
      borderBottomRightRadius: '24px',
      marginBottom: '20px',
      boxShadow: '0 10px 25px -5px rgba(59, 130, 246, 0.3)'
    },
    topRow: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '16px'
    },
    backBtn: {
      width: '40px',
      height: '40px',
      borderRadius: '12px',
      backgroundColor: 'rgba(255, 255, 255, 0.2)', // Glass effect
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      cursor: 'pointer',
      backdropFilter: 'blur(5px)',
      border: '1px solid rgba(255, 255, 255, 0.1)'
    },
    title: {
      fontSize: '26px',
      fontWeight: '800',
      margin: '0',
      letterSpacing: '-0.5px'
    },
    subtitle: {
      fontSize: '14px',
      opacity: 0.9,
      fontWeight: '500',
      marginTop: '4px'
    },
    placeholder: {
      width: '40px'
    }
  };

  return (
    <div style={styles.header}>
      {/* Top Row: Back Button & Right Element */}
      <div style={styles.topRow}>
        {showBack ? (
          <div style={styles.backBtn} onClick={() => navigate(-1)}>
            <ArrowLeft size={20} color="#fff" />
          </div>
        ) : (
          <div style={styles.placeholder}></div> // Keeps spacing balanced if no back button
        )}
        
        {rightElement && <div>{rightElement}</div>}
      </div>

      {/* Title Section */}
      <h1 style={styles.title}>{title}</h1>
      {subtitle && <div style={styles.subtitle}>{subtitle}</div>}
    </div>
  );
};

export default GradientHeader;
