import React from 'react';
import { Bell } from 'lucide-react';

const Dashboard = ({ userName }) => {
  return (
    <div style={{ padding: '16px', paddingBottom: '80px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2 style={{ color: '#2563eb', fontWeight: 'bold' }}>EventSphere</h2>
        <Bell size={24} />
      </header>

      <div style={{ marginTop: '20px' }}>
        <h1 style={{ fontSize: '28px', fontWeight: 'bold' }}>Hi {userName} ðŸ‘‹</h1>
        <p style={{ color: '#94a3b8' }}>Explore college events</p>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px', marginTop: '20px' }}>
        {['Upcoming', 'Registered', 'My Tickets', 'Announcements'].map((label, idx) => (
          <div key={idx} style={{ backgroundColor: '#fff', padding: '16px', borderRadius: '12px', border: '1px solid #f1f5f9' }}>
            <span style={{ color: '#64748b', fontSize: '12px' }}>{label}</span>
            <div style={{ fontSize: '20px', fontWeight: 'bold' }}>{idx + 2}</div>
          </div>
        ))}
      </div>
      {/* Add horizontal scroll for Upcoming Events here */}
    </div>
  );
};

export default Dashboard;
